const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')

async function canReadAnnouncementTemplates ({ authentication: { item: user } }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    return true
}

async function canManageAnnouncementTemplates ({ authentication: { item: user }, originalInput, operation, itemId }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin || user.isSupport) return true


    return false
}

