/**
 * Generated by `createschema acquiring.Payment 'amount:Decimal; currencyCode:Text; time:DateTimeUtc; accountNumber:Text; purpose?:Text; receipts:Relationship:BillingReceipt:PROTECT; multiPayment:Relationship:MultiPayment:PROTECT; context:Relationship:AcquiringIntegrationContext:PROTECT;' --force`
 */

const { AcquiringIntegrationContext } = require('../utils/serverSchema')
const { RESIDENT } = require('@condo/domains/user/constants/common')
const { checkOrganizationPermission } = require('@condo/domains/organization/utils/accessSchema')
const { checkAcquiringIntegrationAccessRight } = require('../utils/accessSchema')

const { throwAuthenticationError } = require('@condo/domains/common/utils/apolloErrorFormatter')
const get = require('lodash/get')

async function canReadPayments ({ authentication: { item: user } }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin || user.isSupport) return {}
    // User can get only it's own payments
    if (user.type === RESIDENT) {
        return {
            multiPayment: { user: { id: user.id } },
        }
    }
    return {
        OR: [
            // Acquiring integration account can see it's payments
            { context: { integration: { accessRights_some: { user: { id: user.id } } } } },
            // Employee with `canReadPayments` can see theirs organization payments
            { context: { organization: { employees_some: { user: { id: user.id }, role: { canReadPayments: true }, deletedAt: null, isBlocked: false } } } },
        ],
    }
}

async function canManagePayments ({ authentication: { item: user }, operation, itemId }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin) return true
    if (operation === 'create') {
        // Nobody can create Payments manually
        return false

    } else if (operation === 'update') {
        if (!itemId) return false
        // Acquiring integration can update it's own Payments
        return { context: { integration: { accessRights_some: { user: { id: user.id } } } } }
    }
    return false
}

async function canReadPaymentsSensitiveData ({ authentication: { item: user }, existingItem, context }) {
    if (user.deletedAt) return false
    if (user.isAdmin || user.isSupport) return true
    const [acquiringContext] = await AcquiringIntegrationContext.getAll(context, { id: existingItem.context })
    if (!acquiringContext) return false
    // If user is acquiring integration
    const integrationId = get(acquiringContext, ['integration', 'id'])
    if (await checkAcquiringIntegrationAccessRight(user.id, integrationId)) return true
    // If user has `canReadPayments` attribute
    const organizationId = get(acquiringContext, ['organization', 'id'])
    return !!(await checkOrganizationPermission(context, user.id, organizationId, 'canReadPayments'))
}


/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadPayments,
    canManagePayments,
    canReadPaymentsSensitiveData,
}
