/**
 * Generated by `createschema acquiring.RecurrentPaymentContext 'limit:Text;paymentDay:Integer;settings:Json;billingCategories:Json'`
 */
const { faker } = require('@faker-js/faker')
const dayjs = require('dayjs')

const { makeLoggedInAdminClient, makeClient, UUID_RE, DATETIME_RE, expectToThrowUniqueConstraintViolationError, waitFor } = require('@open-condo/keystone/test.utils')
const {
    expectToThrowAuthenticationErrorToObj, expectToThrowAuthenticationErrorToObjects,
    expectToThrowAccessDeniedErrorToObj, catchErrorFrom,
} = require('@open-condo/keystone/test.utils')

const {
    RECURRENT_PAYMENT_CONTEXT_BOTH_TRIGGER_SET_UP_ERROR,
    RECURRENT_PAYMENT_CONTEXT_NO_TRIGGER_SET_UP_ERROR,
    RECURRENT_PAYMENT_CONTEXT_PAYMENT_DAY_WRONG_RANGE_ERROR,
} = require('@condo/domains/acquiring/constants/errors')
const {
    RECURRENT_PAYMENT_INIT_STATUS,
} = require('@condo/domains/acquiring/constants/recurrentPayment')
const {
    RecurrentPaymentContext,
    createTestRecurrentPaymentContext,
    updateTestRecurrentPaymentContext,
    RecurrentPayment,
    createTestRecurrentPayment,
} = require('@condo/domains/acquiring/utils/testSchema')
const { createTestBillingCategory } = require('@condo/domains/billing/utils/testSchema')
const { makeClientWithServiceConsumer } = require('@condo/domains/resident/utils/testSchema')
const {
    makeClientWithNewRegisteredAndLoggedInUser,
    makeClientWithSupportUser,
} = require('@condo/domains/user/utils/testSchema')

describe('RecurrentPaymentContext', () => {
    let admin, getContextRequest, billingCategory, serviceConsumerClient

    beforeEach(async () => {
        serviceConsumerClient = await makeClientWithServiceConsumer()
    })

    beforeAll(async () => {
        admin = await makeLoggedInAdminClient()
        billingCategory = (await createTestBillingCategory(admin, { name: `Category ${new Date()}` }))[0]

        getContextRequest = async () => ({
            enabled: false,
            limit: '10000',
            autoPayReceipts: false,
            paymentDay: 10,
            settings: { cardId: faker.datatype.uuid() },
            serviceConsumer: { connect: { id: serviceConsumerClient.serviceConsumer.id } },
            billingCategory: { connect: { id: billingCategory.id } },
        })
    })

    afterAll( () => {
        if (global.gc) {
            global.gc()
        }
    })

    describe('CRUD tests', () => {
        describe('create', () => {
            test('admin can', async () => {
                const admin = await makeLoggedInAdminClient()

                const [obj, attrs] = await createTestRecurrentPaymentContext(admin, await getContextRequest())

                expect(obj.id).toMatch(UUID_RE)
                expect(obj.dv).toEqual(1)
                expect(obj.sender).toEqual(attrs.sender)
                expect(obj.v).toEqual(1)
                expect(obj.newId).toEqual(null)
                expect(obj.deletedAt).toEqual(null)
                expect(obj.createdBy).toEqual(expect.objectContaining({ id: admin.user.id }))
                expect(obj.updatedBy).toEqual(expect.objectContaining({ id: admin.user.id }))
                expect(obj.createdAt).toMatch(DATETIME_RE)
                expect(obj.updatedAt).toMatch(DATETIME_RE)
            })

            test('support can\'t', async () => {
                const client = await makeClientWithSupportUser()

                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await createTestRecurrentPaymentContext(client, await getContextRequest())
                })
            })

            test('user can', async () => {
                const [obj, attrs] = await createTestRecurrentPaymentContext(serviceConsumerClient, await getContextRequest())

                expect(obj.id).toMatch(UUID_RE)
                expect(obj.dv).toEqual(1)
                expect(obj.sender).toEqual(attrs.sender)
                expect(obj.createdBy).toEqual(expect.objectContaining({ id: serviceConsumerClient.user.id }))
            })

            test('anonymous can\'t', async () => {
                const client = await makeClient()

                await expectToThrowAuthenticationErrorToObj(async () => {
                    await createTestRecurrentPaymentContext(client)
                })
            })
        })

        describe('update', () => {
            test('admin can', async () => {
                const admin = await makeLoggedInAdminClient()
                const request = await getContextRequest()
                const [objCreated] = await createTestRecurrentPaymentContext(admin, request)

                const [obj, attrs] = await updateTestRecurrentPaymentContext(admin, objCreated.id, {
                    ...request,
                    enabled: false,
                })

                expect(obj.dv).toEqual(1)
                expect(obj.sender).toEqual(attrs.sender)
                expect(obj.v).toEqual(2)
                expect(obj.updatedBy).toEqual(expect.objectContaining({ id: admin.user.id }))
                expect(obj.enabled).not.toBeTruthy()
            })

            test('support can\'t', async () => {
                const admin = await makeLoggedInAdminClient()
                const request = await getContextRequest()
                const [objCreated] = await createTestRecurrentPaymentContext(admin, request)

                const client = await makeClientWithSupportUser()

                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await updateTestRecurrentPaymentContext(client, objCreated.id, {
                        ...request,
                        enabled: false,
                    })
                })
            })

            test('user can', async () => {
                const request = await getContextRequest()
                const [objCreated] = await createTestRecurrentPaymentContext(serviceConsumerClient, request)

                const [obj, attrs] = await updateTestRecurrentPaymentContext(serviceConsumerClient, objCreated.id, {
                    ...request,
                    enabled: false,
                })

                expect(obj.id).toMatch(UUID_RE)
                expect(obj.dv).toEqual(1)
                expect(obj.sender).toEqual(attrs.sender)
                expect(obj.v).toEqual(2)
                expect(obj.updatedBy).toEqual(expect.objectContaining({ id: serviceConsumerClient.user.id }))
                expect(obj.enabled).not.toBeTruthy()
            })

            test('anonymous can\'t', async () => {
                const admin = await makeLoggedInAdminClient()
                const request = await getContextRequest()
                const [objCreated] = await createTestRecurrentPaymentContext(admin, request)

                const client = await makeClient()
                await expectToThrowAuthenticationErrorToObj(async () => {
                    await updateTestRecurrentPaymentContext(client, objCreated.id, { ...request, enabled: false })
                })
            })
        })

        describe('hard delete', () => {
            test('admin can\'t', async () => {
                const admin = await makeLoggedInAdminClient()
                const [objCreated] = await createTestRecurrentPaymentContext(admin, await getContextRequest())

                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await RecurrentPaymentContext.delete(admin, objCreated.id)
                })
            })

            test('user can\'t', async () => {
                const admin = await makeLoggedInAdminClient()
                const [objCreated] = await createTestRecurrentPaymentContext(admin, await getContextRequest())

                const client = await makeClientWithNewRegisteredAndLoggedInUser()
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await RecurrentPaymentContext.delete(client, objCreated.id)
                })
            })

            test('anonymous can\'t', async () => {
                const admin = await makeLoggedInAdminClient()
                const [objCreated] = await createTestRecurrentPaymentContext(admin, await getContextRequest())

                const client = await makeClient()
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await RecurrentPaymentContext.delete(client, objCreated.id)
                })
            })
        })

        describe('read', () => {
            test('admin can', async () => {
                const admin = await makeLoggedInAdminClient()
                const [obj] = await createTestRecurrentPaymentContext(admin, await getContextRequest())

                const objs = await RecurrentPaymentContext.getAll(admin, {}, { sortBy: ['updatedAt_DESC'] })

                expect(objs.length).toBeGreaterThanOrEqual(1)
                expect(objs).toEqual(expect.arrayContaining([
                    expect.objectContaining({
                        id: obj.id,
                    }),
                ]))
            })

            test('user can', async () => {
                const [obj] = await createTestRecurrentPaymentContext(serviceConsumerClient, await getContextRequest())

                const objs = await RecurrentPaymentContext.getAll(serviceConsumerClient, {}, { sortBy: ['updatedAt_DESC'] })

                expect(objs).toHaveLength(1)
                expect(objs[0]).toMatchObject({
                    id: obj.id,
                })
            })

            test('user1 can\'t read recurrentPaymentContext for user2', async () => {
                const admin = await makeLoggedInAdminClient()
                await createTestRecurrentPaymentContext(admin, await getContextRequest())

                const client2 = await makeClientWithServiceConsumer()
                const objs = await RecurrentPaymentContext.getAll(client2, {}, { sortBy: ['updatedAt_DESC'] })

                expect(objs).toHaveLength(0)
            })


            test('anonymous can\'t', async () => {
                const admin = await makeLoggedInAdminClient()
                await createTestRecurrentPaymentContext(admin, await getContextRequest())

                const client = await makeClient()
                await expectToThrowAuthenticationErrorToObjects(async () => {
                    await RecurrentPaymentContext.getAll(client, {}, { sortBy: ['updatedAt_DESC'] })
                })
            })
        })
    })

    describe('Validation tests', () => {
        test('validate both trigger set up', async () => {
            const admin = await makeLoggedInAdminClient()
            const request = await getContextRequest()
            request.autoPayReceipts = true

            await catchErrorFrom(async () => {
                await createTestRecurrentPaymentContext(admin, request)
            }, ({ errors }) => {
                expect(errors).toMatchObject([{
                    extensions: {
                        code: 'BAD_USER_INPUT',
                        type: RECURRENT_PAYMENT_CONTEXT_BOTH_TRIGGER_SET_UP_ERROR,
                        message: 'You have to set only one trigger',
                    },
                }])
            })
        })
        test('validate no trigger set up', async () => {
            const admin = await makeLoggedInAdminClient()
            const request = await getContextRequest()
            request.autoPayReceipts = false
            request.paymentDay = null

            await catchErrorFrom(async () => {
                await createTestRecurrentPaymentContext(admin, request)
            }, ({ errors }) => {
                expect(errors).toMatchObject([{
                    extensions: {
                        code: 'BAD_USER_INPUT',
                        type: RECURRENT_PAYMENT_CONTEXT_NO_TRIGGER_SET_UP_ERROR,
                        message: 'You have to set at least one trigger',
                    },
                }])
            })
        })
        test('validate omit trigger fields', async () => {
            const admin = await makeLoggedInAdminClient()
            const request = {
                enabled: false,
                limit: '10000',
                settings: { cardId: faker.datatype.uuid() },
                serviceConsumer: { connect: { id: serviceConsumerClient.serviceConsumer.id } },
                billingCategory: { connect: { id: billingCategory.id } },
            }

            await catchErrorFrom(async () => {
                await createTestRecurrentPaymentContext(admin, request)
            }, ({ errors }) => {
                expect(errors).toMatchObject([{
                    extensions: {
                        code: 'BAD_USER_INPUT',
                        type: RECURRENT_PAYMENT_CONTEXT_NO_TRIGGER_SET_UP_ERROR,
                        message: 'You have to set at least one trigger',
                    },
                }])
            })
        })
        test('validate both trigger set up in update paymentDay + autoPayReceipts', async () => {
            const admin = await makeLoggedInAdminClient()
            const request = await getContextRequest()

            const [objCreated] = await createTestRecurrentPaymentContext(admin, request)

            await catchErrorFrom(async () => {
                await updateTestRecurrentPaymentContext(admin, objCreated.id, {
                    ...request,
                    autoPayReceipts: true,
                })
            }, ({ errors }) => {
                expect(errors).toMatchObject([{
                    extensions: {
                        code: 'BAD_USER_INPUT',
                        type: RECURRENT_PAYMENT_CONTEXT_BOTH_TRIGGER_SET_UP_ERROR,
                        message: 'You have to set only one trigger',
                    },
                }])
            })
        })
        test('validate both trigger set up in update autoPayReceipts + paymentDay', async () => {
            const admin = await makeLoggedInAdminClient()
            const request = await getContextRequest()
            request.autoPayReceipts = true
            request.paymentDay = null

            const [objCreated] = await createTestRecurrentPaymentContext(admin, request)

            await catchErrorFrom(async () => {
                await updateTestRecurrentPaymentContext(admin, objCreated.id, {
                    ...request,
                    paymentDay: 10,
                })
            }, ({ errors }) => {
                expect(errors).toMatchObject([{
                    extensions: {
                        code: 'BAD_USER_INPUT',
                        type: RECURRENT_PAYMENT_CONTEXT_BOTH_TRIGGER_SET_UP_ERROR,
                        message: 'You have to set only one trigger',
                    },
                }])
            })
        })
        test('validate billingCategory is unique for serviceConsumer', async () => {
            const admin = await makeLoggedInAdminClient()

            await createTestRecurrentPaymentContext(admin, await getContextRequest())

            await expectToThrowUniqueConstraintViolationError(async () => {
                await createTestRecurrentPaymentContext(admin, await getContextRequest())
            }, 'recurrentPaymentContext_unique_serviceConsumer_and_billingCategory')
        })
        test('validate serviceConsumer', async () => {
            const resident = await makeClientWithServiceConsumer()
            await catchErrorFrom(async () => {
                await createTestRecurrentPaymentContext(resident, await getContextRequest())
            }, ({ errors }) => {
                expect(errors).toMatchObject([{
                    name: 'GraphQLError',
                    message: 'Unable to connect a RecurrentPaymentContext.serviceConsumer<ServiceConsumer>',
                }])
            })
        })
        test('validate paymentDay not in range 1-31', async () => {
            const admin = await makeLoggedInAdminClient()
            const request = await getContextRequest()
            request.paymentDay = 0

            await catchErrorFrom(async () => {
                await createTestRecurrentPaymentContext(admin, {
                    ...request,
                    paymentDay: 0,
                })
            }, ({ errors }) => {
                expect(errors).toMatchObject([{
                    extensions: {
                        code: 'BAD_USER_INPUT',
                        type: RECURRENT_PAYMENT_CONTEXT_PAYMENT_DAY_WRONG_RANGE_ERROR,
                        message: 'You have to set value for paymentDay in range 1-31',
                    },
                }])
            })

            await catchErrorFrom(async () => {
                await createTestRecurrentPaymentContext(admin, {
                    ...request,
                    paymentDay: -1,
                })
            }, ({ errors }) => {
                expect(errors).toMatchObject([{
                    extensions: {
                        code: 'BAD_USER_INPUT',
                        type: RECURRENT_PAYMENT_CONTEXT_PAYMENT_DAY_WRONG_RANGE_ERROR,
                        message: 'You have to set value for paymentDay in range 1-31',
                    },
                }])
            })

            await catchErrorFrom(async () => {
                await createTestRecurrentPaymentContext(admin, {
                    ...request,
                    paymentDay: 32,
                })
            }, ({ errors }) => {
                expect(errors).toMatchObject([{
                    extensions: {
                        code: 'BAD_USER_INPUT',
                        type: RECURRENT_PAYMENT_CONTEXT_PAYMENT_DAY_WRONG_RANGE_ERROR,
                        message: 'You have to set value for paymentDay in range 1-31',
                    },
                }])
            })
        })
    })

    describe('Hooks tests', () => {
        test('ServiceConsumer change should remove outdated recurrent payments', async () => {
            const admin = await makeLoggedInAdminClient()
            const { serviceConsumer: { id: newServiceConsumerId } } = await makeClientWithServiceConsumer()
            const [recurrentPaymentContext] = await createTestRecurrentPaymentContext(admin, await getContextRequest())
            await createTestRecurrentPayment(admin, {
                status: RECURRENT_PAYMENT_INIT_STATUS,
                payAfter: dayjs().toISOString(),
                tryCount: 0,
                state: {},
                billingReceipts: [ { id: faker.datatype.uuid() }],
                recurrentPaymentContext: { connect: { id: recurrentPaymentContext.id } },
            })

            // update service consumer
            await updateTestRecurrentPaymentContext(admin, recurrentPaymentContext.id, {
                serviceConsumer: { connect: { id: newServiceConsumerId } },
            })

            await waitFor(async () => {
                const recurrentPayments = await RecurrentPayment.getAll(admin, {
                    recurrentPaymentContext: { id: recurrentPaymentContext.id },
                })

                expect(recurrentPayments).toHaveLength(0)
            })
        })
        test('PaymentDay change that was eq today should remove outdated recurrent payments', async () => {
            const admin = await makeLoggedInAdminClient()
            const createRecurrentPaymentContextRequest = await getContextRequest()
            createRecurrentPaymentContextRequest.paymentDay = dayjs().date()
            const [recurrentPaymentContext] = await createTestRecurrentPaymentContext(admin, createRecurrentPaymentContextRequest)
            await createTestRecurrentPayment(admin, {
                status: RECURRENT_PAYMENT_INIT_STATUS,
                payAfter: dayjs().toISOString(),
                tryCount: 0,
                state: {},
                billingReceipts: [ { id: faker.datatype.uuid() }],
                recurrentPaymentContext: { connect: { id: recurrentPaymentContext.id } },
            })

            // update service consumer
            await updateTestRecurrentPaymentContext(admin, recurrentPaymentContext.id, {
                paymentDay: dayjs().add(1, 'day').date(),
            })

            await waitFor(async () => {
                const recurrentPayments = await RecurrentPayment.getAll(admin, {
                    recurrentPaymentContext: { id: recurrentPaymentContext.id },
                })

                expect(recurrentPayments).toHaveLength(0)
            })
        })
        test('PaymentDay change that was\'t eq today should not remove recurrent payments', async () => {
            const admin = await makeLoggedInAdminClient()
            const createRecurrentPaymentContextRequest = await getContextRequest()
            createRecurrentPaymentContextRequest.paymentDay = dayjs().add(1, 'day').date()
            const [recurrentPaymentContext] = await createTestRecurrentPaymentContext(admin, createRecurrentPaymentContextRequest)
            await createTestRecurrentPayment(admin, {
                status: RECURRENT_PAYMENT_INIT_STATUS,
                payAfter: dayjs().toISOString(),
                tryCount: 0,
                state: {},
                billingReceipts: [ { id: faker.datatype.uuid() }],
                recurrentPaymentContext: { connect: { id: recurrentPaymentContext.id } },
            })

            // update service consumer
            await updateTestRecurrentPaymentContext(admin, recurrentPaymentContext.id, {
                paymentDay: dayjs().add(2, 'day').date(),
            })

            const recurrentPayments = await RecurrentPayment.getAll(admin, {
                recurrentPaymentContext: { id: recurrentPaymentContext.id },
            })

            expect(recurrentPayments).toHaveLength(1)
        })
        test('Trigger change from paymentDat to autoPay should remove outdated recurrent payments', async () => {
            const admin = await makeLoggedInAdminClient()
            const createRecurrentPaymentContextRequest = await getContextRequest()
            const [recurrentPaymentContext] = await createTestRecurrentPaymentContext(admin, createRecurrentPaymentContextRequest)
            await createTestRecurrentPayment(admin, {
                status: RECURRENT_PAYMENT_INIT_STATUS,
                payAfter: dayjs().toISOString(),
                tryCount: 0,
                state: {},
                billingReceipts: [ { id: faker.datatype.uuid() }],
                recurrentPaymentContext: { connect: { id: recurrentPaymentContext.id } },
            })

            // update service consumer
            await updateTestRecurrentPaymentContext(admin, recurrentPaymentContext.id, {
                paymentDay: null,
                autoPayReceipts: true,
            })

            await waitFor(async () => {
                const recurrentPayments = await RecurrentPayment.getAll(admin, {
                    recurrentPaymentContext: { id: recurrentPaymentContext.id },
                })

                expect(recurrentPayments).toHaveLength(0)
            })
        })
        test('Trigger change from autoPay to paymentDate should remove outdated recurrent payments', async () => {
            const admin = await makeLoggedInAdminClient()
            const createRecurrentPaymentContextRequest = await getContextRequest()
            createRecurrentPaymentContextRequest.paymentDay = null
            createRecurrentPaymentContextRequest.autoPayReceipts = true
            const [recurrentPaymentContext] = await createTestRecurrentPaymentContext(admin, createRecurrentPaymentContextRequest)
            await createTestRecurrentPayment(admin, {
                status: RECURRENT_PAYMENT_INIT_STATUS,
                payAfter: dayjs().toISOString(),
                tryCount: 0,
                state: {},
                billingReceipts: [ { id: faker.datatype.uuid() }],
                recurrentPaymentContext: { connect: { id: recurrentPaymentContext.id } },
            })

            // update service consumer
            await updateTestRecurrentPaymentContext(admin, recurrentPaymentContext.id, {
                paymentDay: dayjs().add(1, 'day').date(),
                autoPayReceipts: false,
            })

            await waitFor(async () => {
                const recurrentPayments = await RecurrentPayment.getAll(admin, {
                    recurrentPaymentContext: { id: recurrentPaymentContext.id },
                })

                expect(recurrentPayments).toHaveLength(0)
            })
        })
    })
})
