/**
 * Generated by `createschema contact.Contact 'property:Relationship:Property:SET_NULL; name:Text; phone:Text; unitName?:Text; email?:Text;'`
 */

const { checkOrganizationPermission } = require('@condo/domains/organization/utils/accessSchema')
const get = require('lodash/get')
const { throwAuthenticationError } = require('@condo/domains/common/utils/apolloErrorFormatter')
const { Contact } = require('../utils/serverSchema')
const { checkRelatedOrganizationPermission } = require('@condo/domains/organization/utils/accessSchema')
const { queryOrganizationEmployeeFromRelatedOrganizationFor } = require('@condo/domains/organization/utils/accessSchema')
const { queryOrganizationEmployeeFor } = require('@condo/domains/organization/utils/accessSchema')

async function canReadContacts({ authentication: { item: user } }) {
    if (!user) return throwAuthenticationError()
    if (user.isAdmin) return {}
    const userId = user.id
    return {
        organization: {
            OR: [queryOrganizationEmployeeFor(userId), queryOrganizationEmployeeFromRelatedOrganizationFor(userId)],
        },
    }
}

async function canManageContacts({ authentication: { item: user }, originalInput, operation, itemId, context }) {
    if (!user) return throwAuthenticationError()
    if (user.isAdmin) return true
    if (operation === 'create') {
        const organizationId = get(originalInput, ['organization', 'connect', 'id'])
        const canManageRelatedOrganizationContacts = await checkRelatedOrganizationPermission(
            context,
            user.id,
            organizationId,
            'canManageContacts',
        )
        if (canManageRelatedOrganizationContacts) {
            return true
        }
        const canManageContacts = await checkOrganizationPermission(context, user.id, organizationId, 'canManageContacts')
        return canManageContacts
    } else if (operation === 'update') {
        const [contact] = await Contact.getAll(context, { id: itemId })
        if (!contact) {
            return false
        }
        const canManageRelatedOrganizationContacts = await checkRelatedOrganizationPermission(
            context,
            user.id,
            contact.organization.id,
            'canManageContacts',
        )
        if (canManageRelatedOrganizationContacts) {
            return true
        }
        const canManageContacts = await checkOrganizationPermission(
            context,
            user.id,
            contact.organization.id,
            'canManageContacts',
        )
        return canManageContacts
    }
    return false
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadContacts,
    canManageContacts,
}
