/**
 * Generated by `createschema contact.ContactExportTask 'status:Select:processing,completed,error; format:Select:excel;exportedRecordsCount:Integer; totalRecordsCount:Integer; file?:File; meta?:Json'`
 */

const { Text, Integer, Select, File, Relationship } = require('@keystonejs/fields')

const conf = require('@open-condo/config')
const { canOnlyServerSideWithoutUserRequest } = require('@open-condo/keystone/access')
const { Json } = require('@open-condo/keystone/fields')
const { historical, versioned, uuided, tracked, softDeleted, dvAndSender } = require('@open-condo/keystone/plugins')
const { GQLListSchema } = require('@open-condo/keystone/schema')
const { extractReqLocale } = require('@open-condo/locales/extractReqLocale')

const { PROCESSING, EXPORT_STATUS_VALUES, EXCEL } = require('@condo/domains/common/constants/export')
const { COMPLETED, ERROR } = require('@condo/domains/common/constants/export')
const FileAdapter = require('@condo/domains/common/utils/fileAdapter')
const { getFileMetaAfterChange } = require('@condo/domains/common/utils/fileAdapter')
const { normalizeTimeZone } = require('@condo/domains/common/utils/timezone')
const access = require('@condo/domains/contact/access/ContactExportTask')
const { exportContacts } = require('@condo/domains/contact/tasks')
const { DEFAULT_ORGANIZATION_TIMEZONE } = require('@condo/domains/organization/constants/common')

const ContactExportTaskFileAdapter = new FileAdapter('ContactExportTask')
const setFileMetaAfterChange = getFileMetaAfterChange(ContactExportTaskFileAdapter, 'file')


const ContactExportTask = new GQLListSchema('ContactExportTask', {
    schemaDoc: 'Stores requested export format, status of export job, link to exported file and information about progress of export job',
    fields: {

        status: {
            schemaDoc: 'Status of the job. Used at client side progress information component',
            type: Select,
            options: EXPORT_STATUS_VALUES,
            defaultValue: PROCESSING,
            isRequired: true,
            access: {
                create: canOnlyServerSideWithoutUserRequest,
                read: true,
                update: true,
            },
        },

        format: {
            schemaDoc: 'Requested export file format',
            type: Select,
            options: [EXCEL],
            isRequired: true,
            access: {
                create: true,
                read: true,
                update: canOnlyServerSideWithoutUserRequest,
            },
        },

        exportedRecordsCount: {
            schemaDoc: 'How many records at the moment are exported',
            type: Integer,
            isRequired: true,
            defaultValue: 0,
            access: {
                create: canOnlyServerSideWithoutUserRequest,
                read: true,
                update: canOnlyServerSideWithoutUserRequest,
            },
        },

        totalRecordsCount: {
            schemaDoc: 'Total records to export that will be determined at server side in export operation',
            type: Integer,
            isRequired: false,
            defaultValue: 0,
            access: {
                create: canOnlyServerSideWithoutUserRequest,
                read: true,
                update: canOnlyServerSideWithoutUserRequest,
            },
        },

        file: {
            schemaDoc: 'Meta information about file, saved outside of database somewhere. Shape of meta information JSON object is specific to file adapter, used by saving a file.',
            type: File,
            adapter: ContactExportTaskFileAdapter,
            access: {
                create: canOnlyServerSideWithoutUserRequest,
                read: true,
                update: canOnlyServerSideWithoutUserRequest,
            },
        },

        meta: {
            schemaDoc: 'Stores information about query and ids of exported and failed records',
            type: Json,
            access: {
                create: canOnlyServerSideWithoutUserRequest,
                read: true,
                update: canOnlyServerSideWithoutUserRequest,
            },
        },

        where: {
            schemaDoc: 'Filtering conditions for records to export',
            type: Json,
            isRequired: true,
            access: {
                read: true,
                create: true,
                update: false,
            },
        },

        sortBy: {
            schemaDoc: 'Sorting parameters for records to export',
            type: Json,
            isRequired: true,
            access: {
                read: true,
                create: true,
                update: false,
            },
        },

        locale: {
            schemaDoc: 'Requested export locale, in that the resulting file will be rendered',
            type: Text,
            isRequired: true,
            hooks: {
                resolveInput: async ({ context }) => {
                    return extractReqLocale(context.req) || conf.DEFAULT_LOCALE
                },
            },
            access: {
                read: true,
                create: true,
                update: false,
            },
        },

        timeZone: {
            schemaDoc: 'To requested timeZone all datetime fields will be converted',
            type: Text,
            isRequired: true,
            hooks: {
                resolveInput: async ({ resolvedData }) => {
                    const { timeZone } = resolvedData
                    return normalizeTimeZone(timeZone) || DEFAULT_ORGANIZATION_TIMEZONE
                },
            },
            access: {
                read: true,
                create: true,
                update: false,
            },
        },

        user: {
            schemaDoc: 'User that requested this exporting operation. Will be used for read access checks to display all exported tasks somewhere and to display progress indicator of ongoing exporting task for current user',
            type: Relationship,
            ref: 'User',
            isRequired: true,
            kmigratorOptions: { null: false, on_delete: 'models.CASCADE' },
            knexOptions: { isNotNullable: true },
            access: {
                read: true,
                create: true,
                update: false,
            },
        },
    },
    hooks: {
        validateInput: async ({ resolvedData, existingItem, addValidationError }) => {
            if (existingItem) {
                if (resolvedData['status'] && existingItem['status'] === COMPLETED) {
                    addValidationError('status is already completed')
                }
                if (resolvedData['status'] && existingItem['status'] === ERROR) {
                    addValidationError('status is already error')
                }
            }
        },
        afterChange: async (args) => {
            const { updatedItem, operation } = args
            await setFileMetaAfterChange(args)

            if (operation === 'create') {
                await exportContacts.delay(updatedItem.id)
            }
        },
    },
    plugins: [uuided(), versioned(), tracked(), softDeleted(), dvAndSender(), historical()],
    access: {
        read: access.canReadContactExportTasks,
        create: access.canManageContactExportTasks,
        update: access.canManageContactExportTasks,
        delete: false,
        auth: true,
    },
})

module.exports = {
    ContactExportTask,
}
