/**
 * Generated by `createschema marketplace.Invoice 'number:Integer; property:Relationship:Property:PROTECT; unitType:Text; unitName:Text; accountNumber:Text; toPay:Decimal; items:Json; contact?:Relationship:Contact:SET_NULL; client?:Relationship:User:SET_NULL; clientName?:Text; clientPhone?:Text; clientEmail?:Text'`
 */

const { get } = require('lodash')

const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')
const { getById } = require('@open-condo/keystone/schema')

const {
    INVOICE_STATUS_PUBLISHED,
    INVOICE_STATUS_PAID,
    INVOICE_STATUS_DRAFT,
    INVOICE_STATUS_CANCELED,
} = require('@condo/domains/marketplace/constants')
const { canReadObjectsAsB2BAppServiceUser, canManageObjectsAsB2BAppServiceUser } = require('@condo/domains/miniapp/utils/b2bAppServiceUserAccess/server.utils')
const {
    getEmployedOrRelatedOrganizationsByPermissions,
    checkPermissionsInEmployedOrRelatedOrganizations,
} = require('@condo/domains/organization/utils/accessSchema')
const { RESIDENT, SERVICE } = require('@condo/domains/user/constants/common')


async function canReadInvoices (args) {
    const { authentication: { item: user }, context } = args

    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    if (user.isAdmin || user.isSupport) return {}

    if (user.type === RESIDENT) {
        return {
            deletedAt: null,
            client: { id: user.id },
            OR: [
                { status_in: [INVOICE_STATUS_PUBLISHED, INVOICE_STATUS_PAID, INVOICE_STATUS_CANCELED] },
                {
                    AND: [
                        { status: INVOICE_STATUS_DRAFT },
                        { createdBy: { type: RESIDENT } },
                    ],
                },
            ],
        }
    }

    // The service user may make a call from billing integration.
    // The user from integration has no b2b access rights.
    // So, we return true if the service user has explicit rights.
    // Otherwise, we continue the checking.
    let filterByOrganizationIds = []
    if (user.type === SERVICE) {
        const canReadAsB2BServiceUser = await canReadObjectsAsB2BAppServiceUser(args)
        if (canReadAsB2BServiceUser === true) {
            return true
        }

        filterByOrganizationIds = canReadAsB2BServiceUser
    }

    const permittedOrganizations = await getEmployedOrRelatedOrganizationsByPermissions(context, user, 'canReadInvoices')

    return {
        OR: [
            { organization: { id_in: permittedOrganizations } },
            filterByOrganizationIds,
        ],
        deletedAt: null,
    }
}

async function canManageInvoices (args) {
    const { authentication: { item: user }, originalInput, operation, itemId, context } = args

    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin || user.isSupport) return true
    if (user.type === RESIDENT) return false

    if (user.type === SERVICE) {
        return await canManageObjectsAsB2BAppServiceUser(args)
    }

    let organizationId

    if (operation === 'create') {
        organizationId = get(originalInput, ['organization', 'connect', 'id'])
    } else if (operation === 'update') {
        if (!itemId) return false
        const item = await getById('Invoice', itemId)
        organizationId = get(item, 'organization')
    }

    if (!organizationId) return false
    return await checkPermissionsInEmployedOrRelatedOrganizations(context, user, organizationId, 'canManageInvoices')
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadInvoices,
    canManageInvoices,
}
