/**
 * Generated by `createschema marketplace.InvoiceWebhookDelivery 'invoice:Relationship:Invoice:CASCADE; ...'`
 */

const get = require('lodash/get')

const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')
const { getById } = require('@open-condo/keystone/schema')

const { canReadObjectsAsB2BAppServiceUser, canManageObjectsAsB2BAppServiceUser } = require('@condo/domains/miniapp/utils/b2bAppServiceUserAccess/server.utils')
const {
    getEmployedOrRelatedOrganizationsByPermissions,
    checkPermissionsInEmployedOrRelatedOrganizations,
} = require('@condo/domains/organization/utils/accessSchema')
const { SERVICE } = require('@condo/domains/user/constants/common')


async function canReadInvoiceWebhookDeliveries (args) {
    const { authentication: { item: user }, context } = args

    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    if (user.isAdmin || user.isSupport) return {}

    if (user.type === SERVICE) {
        return await canReadObjectsAsB2BAppServiceUser(args)
    }

    // Users can read webhook deliveries for invoices in their organizations
    const permittedOrganizations = await getEmployedOrRelatedOrganizationsByPermissions(context, user, 'canReadInvoices')

    return {
        invoice: {
            organization: {
                id_in: permittedOrganizations,
            },
        },
        deletedAt: null,
    }
}

async function canManageInvoiceWebhookDeliveries (args) {
    const { authentication: { item: user }, originalInput, operation, itemId, context } = args

    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin || user.isSupport) return true

    if (user.type === SERVICE) {
        return await canManageObjectsAsB2BAppServiceUser(args)
    }

    let organizationId

    if (operation === 'create') {
        const invoiceId = get(originalInput, ['invoice', 'connect', 'id'])
        if (!invoiceId) return false
        const invoice = await getById('Invoice', invoiceId)
        organizationId = get(invoice, 'organization')
    } else if (operation === 'update') {
        if (!itemId) return false
        const item = await getById('InvoiceWebhookDelivery', itemId)
        if (!item) return false
        const invoice = await getById('Invoice', item.invoice)
        organizationId = get(invoice, 'organization')
    }

    if (!organizationId) return false
    return await checkPermissionsInEmployedOrRelatedOrganizations(context, user, organizationId, 'canManageInvoices')
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadInvoiceWebhookDeliveries,
    canManageInvoiceWebhookDeliveries,
}
