/**
 * Generated by `createschema marketplace.MarketItemFile 'marketItem:Relationship:MarketItem:CASCADE; file:File;'`
 */

const compact = require('lodash/compact')
const get = require('lodash/get')
const uniq = require('lodash/uniq')

const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')
const { getById } = require('@open-condo/keystone/schema')

const {
    checkPermissionsInEmployedOrRelatedOrganizations,
    getEmployedOrRelatedOrganizationsByPermissions,
} = require('@condo/domains/organization/utils/accessSchema')
const { getUserResidents, getUserServiceConsumers } = require('@condo/domains/resident/utils/accessSchema')
const { RESIDENT } = require('@condo/domains/user/constants/common')
const { STAFF } = require('@condo/domains/user/constants/common')

async function canReadMarketItemFiles ({ authentication: { item: user }, context }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin || user.isSupport) return {}
    if (user.type === RESIDENT) {
        // TODO (DOMA-7503) use PriceScope to check access
        const userResidents = await getUserResidents(context, user)
        if (!userResidents.length) return false
        const residentOrganizationsIds = compact(userResidents.map(resident => get(resident, 'organization')))
        const userServiceConsumers = await getUserServiceConsumers(context, user)
        const serviceConsumerOrganizationIds = userServiceConsumers.map(sc => sc.organization)
        const organizationIds = [...residentOrganizationsIds, ...serviceConsumerOrganizationIds]
        if (organizationIds.length) {
            return {
                marketItem: {
                    organization: { id_in: uniq(organizationIds) },
                },
            }
        }
        return false
    }

    const permittedOrganizations = await getEmployedOrRelatedOrganizationsByPermissions(context, user, 'canReadMarketItems')

    return {
        OR: [
            {
                AND: [
                    {
                        marketItem: {
                            organization: {
                                id_in: permittedOrganizations,
                            },
                        },
                    },
                ],
            },
            { createdBy: { id: user.id } },
        ],
    }
}

async function canManageMarketItemFiles ({ authentication: { item: user }, originalInput, operation, itemId, context }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin || user.isSupport) return true

    if (user.type === RESIDENT) return false

    if (user.type === STAFF) {
        let marketItem
        if (operation === 'create') {
            const marketItemId = get(originalInput, ['marketItem', 'connect', 'id'], null)
            // all staff users can create files without market items
            if (!marketItemId) return true

            marketItem = await getById('MarketItem', marketItemId)
        } else if (operation === 'update') {
            const marketItemFile = await getById('MarketItemFile', itemId)
            const resolvedMarketItemId = get(originalInput, ['marketItem', 'connect', 'id'], null)

            if (resolvedMarketItemId) {
                marketItem = await getById('MarketItem', resolvedMarketItemId)
            } else if (get(marketItemFile, 'marketItem')) {
                marketItem = await getById('MarketItem', marketItemFile.marketItem)
            } else {
                return get(marketItemFile, 'createdBy') === user.id
            }
        }

        const organizationId = get(marketItem, 'organization', null)
        if (!organizationId) return false

        return await checkPermissionsInEmployedOrRelatedOrganizations(context, user, organizationId, 'canManageMarketItems')
    }

    return false
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadMarketItemFiles,
    canManageMarketItemFiles,
}
