/**
 * Generated by `createschema meter.MeterReading 'organization:Relationship:Organization:CASCADE; property:Relationship:Property:CASCADE; account?:Relationship:BillingAccount:SET_NULL; billingAccountMeter?:Relationship:BillingAccountMeter:SET_NULL; date:DateTimeUtc; meter:Relationship:Meter:CASCADE; value:Integer; source:Relationship:MeterReadingSource:PROTECT'`
 */
const get = require('lodash/get')
const uniq = require('lodash/uniq')

const { isSoftDelete } = require('@open-condo/keystone/access')
const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')
const { getById, getByCondition, find } = require('@open-condo/keystone/schema')

const { getAvailableResidentMeters } = require('@condo/domains/meter/utils/serverSchema')
const {
    canReadObjectsAsB2BAppServiceUser,
    canManageObjectsAsB2BAppServiceUser,
} = require('@condo/domains/miniapp/utils/b2bAppServiceUserAccess')
const {
    getEmployedOrRelatedOrganizationsByPermissions,
    checkPermissionsInEmployedOrRelatedOrganizations,
} = require('@condo/domains/organization/utils/accessSchema')
const { RESIDENT, SERVICE } = require('@condo/domains/user/constants/common')


async function canReadMeterReadings (args) {
    const { authentication: { item: user }, context } = args

    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    
    if (user.isSupport || user.isAdmin) return {}

    if (user.type === RESIDENT) {
        const availableMeters = await getAvailableResidentMeters(user.id)
        const availableMeterIds = availableMeters.map((meter) => meter.id)

        return {
            meter: { id_in: availableMeterIds, deletedAt: null },
            deletedAt: null,
        }
    }

    if (user.type === SERVICE) {
        return await canReadObjectsAsB2BAppServiceUser(args)
    }

    const permittedOrganizations = await getEmployedOrRelatedOrganizationsByPermissions(context, user, 'canReadMeters')

    return {
        organization: {
            id_in: permittedOrganizations,
        },
    }
}

async function canManageMeterReadings (args) {
    const { authentication: { item: user }, originalInput, operation, itemId, itemIds, context } = args

    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin) return true

    if (user.type === SERVICE) {
        return await canManageObjectsAsB2BAppServiceUser(args)
    }

    const isBulkRequest = Array.isArray(originalInput)

    if (operation === 'create') {
        const meterId = get(originalInput, ['meter', 'connect', 'id'], null)
        if (!meterId) return false

        if (user.type === RESIDENT) {
            const availableMeters = await getAvailableResidentMeters(user.id)
            return availableMeters.some((meter) => meter.id === meterId && !meter.isAutomatic)
        }

        const meter = await getById('Meter', meterId)
        const meterOrganization = get(meter, 'organization', null)
        if (!meterOrganization) return false

        return await checkPermissionsInEmployedOrRelatedOrganizations(context, user, meterOrganization, 'canManageMeterReadings')
    }

    if (operation === 'update') {
        if (isBulkRequest) {
            if (!itemIds || !Array.isArray(itemIds)) return false
            if (itemIds.length !== uniq(itemIds).length) return false

            const meterReadings = await find('MeterReading', {
                id_in: itemIds,
                deletedAt: null,
            })
            if (meterReadings.length !== itemIds.length) return false

            const organizationIds = uniq(meterReadings.map(reading => get(reading, 'organization')))
            if (organizationIds.length !== 1) return false

            return await checkPermissionsInEmployedOrRelatedOrganizations(context, user, organizationIds, 'canManageMeterReadings')

        } else {
            if (!itemId) return false
            const meterReading = await getByCondition('MeterReading', {
                id: itemId,
                deletedAt: null,
            })
            if (!meterReading) return false
    
            const meterReadingOrganization = get(meterReading, 'organization')
            if (!meterReadingOrganization) return false
    
            return await checkPermissionsInEmployedOrRelatedOrganizations(context, user, meterReadingOrganization, 'canManageMeterReadings')
        }
       
    }

    return false
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadMeterReadings,
    canManageMeterReadings,
}
