/**
 * Generated by `createschema meter.MeterReadingExportTask 'status:Select:processing,completed,error; format:Select:excel; exportedRecordsCount:Integer; totalRecordsCount:Integer; file?:File; meta?:Json; where:Json; sortBy:Json; locale:Text; timeZone:Text; user:Relationship:User:CASCADE;'`
 */

const compact = require('lodash/compact')
const get = require('lodash/get')
const isEmpty = require('lodash/isEmpty')
const uniq = require('lodash/uniq')

const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')
const { getById } = require('@open-condo/keystone/schema')

const { CANCELLED } = require('@condo/domains/common/constants/export')
const { checkPermissionsInEmployedOrRelatedOrganizations } = require('@condo/domains/organization/utils/accessSchema')


async function canReadMeterReadingExportTasks ({ authentication: { item: user } }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    if (user.isAdmin) return {}

    return { user: { id: user.id } }
}

async function canManageMeterReadingExportTasks ({ authentication: { item: user }, context, originalInput, operation, itemId }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin) return true

    if (operation === 'create') {
        if (get(originalInput, ['user', 'connect', 'id']) !== user.id) return false

        const organizationId = get(originalInput, ['where', 'organization', 'id'])
        const organizationIdIn = get(originalInput, ['where', 'organization', 'id_in'], [])
        const organizationIds = uniq(compact([organizationId, ...organizationIdIn]))

        if (isEmpty(organizationIds) || organizationIds.some(id => typeof id !== 'string')) return false

        return await checkPermissionsInEmployedOrRelatedOrganizations(context, user, organizationIds, 'canReadMeters')
    } else if (operation === 'update') {
        if (!itemId) return false

        const task = await getById('MeterReadingExportTask', itemId)
        if (task.deletedAt) {
            return false
        }

        if (get(task, 'user') === user.id && get(originalInput, 'status') === CANCELLED) {
            return true
        }
    }

    return false
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadMeterReadingExportTasks,
    canManageMeterReadingExportTasks,
}
