/**
 * Generated by `createschema meter.MeterReading 'organization:Relationship:Organization:CASCADE; property:Relationship:Property:CASCADE; account?:Relationship:BillingAccount:SET_NULL; billingAccountMeter?:Relationship:BillingAccountMeter:SET_NULL; date:DateTimeUtc; meter:Relationship:Meter:CASCADE; value:Integer; source:Relationship:MeterReadingSource:PROTECT'`
 */

const { Text, Relationship, Integer, Select, Checkbox, DateTimeUtc, CalendarDay, Decimal, Password, File } = require('@keystonejs/fields')
const { Json } = require('@core/keystone/fields')
const { GQLListSchema } = require('@core/keystone/schema')
const { historical, versioned, uuided, tracked, softDeleted } = require('@core/keystone/plugins')
const { SENDER_FIELD, DV_FIELD } = require('@condo/domains/common/schema/fields')
const access = require('@condo/domains/meter/access/MeterReading')
const { VALUE_LESS_THAN_PREVIOUS_ERROR } = require('@condo/domains/meter/constants/errors')
const { ORGANIZATION_OWNED_FIELD } = require('../../../schema/_common')
const { MeterReading: MeterReadingApi } = require('../utils/serverSchema')
const moment = require('moment')
const { TIME_IS_UP_ERROR } = require('../constants/errors')


const MeterReading = new GQLListSchema('MeterReading', {
    schemaDoc: 'Meter reading taken from a client or billing',
    fields: {
        dv: DV_FIELD,
        sender: SENDER_FIELD,

        organization: ORGANIZATION_OWNED_FIELD,

        property: {
            schemaDoc: 'Property related to the MeterReading',
            type: Relationship,
            ref: 'Property',
            isRequired: true,
            knexOptions: { isNotNullable: true }, // Required relationship only!
            kmigratorOptions: { null: false, on_delete: 'models.CASCADE' },
        },

        account: {
            schemaDoc: 'Client\'s billing account',
            type: Relationship,
            ref: 'BillingAccount',
        },

        billingAccountMeter: {
            schemaDoc: 'Link to BillingAccountMeter if it exist in billing context',
            type: Relationship,
            ref: 'BillingAccountMeter',
        },

        date: {
            schemaDoc: 'Date when the readings were taken',
            type: DateTimeUtc,
            isRequired: true,
        },

        meter: {
            schemaDoc: 'Meter from which readings were taken',
            type: Relationship,
            ref: 'Meter',
            isRequired: true,
            knexOptions: { isNotNullable: true }, // Required relationship only!
            kmigratorOptions: { null: false, on_delete: 'models.CASCADE' },
        },

        value: {
            schemaDoc: 'Numerical value on the meter at the time of taking readings',
            type: Integer,
            isRequired: true,
            hooks: {
                validateInput: async ({ context, operation, itemId, resolvedData, fieldPath, addFieldValidationError }) => {
                    if (operation === 'update') {
                        const value = resolvedData[fieldPath]

                        const [lastMeterReading] = await MeterReadingApi.getAll(context, { id: itemId }, {
                            sortBy: ['createdAt_DESC'],
                        })

                        if (lastMeterReading && lastMeterReading.value > value)
                            addFieldValidationError(`${VALUE_LESS_THAN_PREVIOUS_ERROR}value] Meter reading value less than previous`)
                    }
                },
            },
        },

        source: {
            schemaDoc: 'Meter reading source channel/system. Examples: call, mobile_app, billing, ...',
            type: Relationship,
            ref: 'MeterReadingSource',
            isRequired: true,
            knexOptions: { isNotNullable: true }, // Required relationship only!
            kmigratorOptions: { null: false, on_delete: 'models.PROTECT' },
        },

    },
    hooks: {
        validateInput: async ({ context, operation, itemId, resolvedData, fieldPath, addFieldValidationError }) => {
            // if 2 minutes have passed since the moment the readings were taken, then they cannot be changed
            if (operation === 'update') {
                const [meterReading] = await MeterReadingApi.getAll(context, { id: itemId }, {
                    sortBy: ['createdAt_DESC'],
                })
                const createdDate = moment(meterReading.createdAt)
                const now = moment()
                const diffInMinutes = now.diff(createdDate, 'minutes')

                if (!meterReading || diffInMinutes > 2) {
                    addFieldValidationError(`${TIME_IS_UP_ERROR}date] 2 minutes have passed since MeterReading was created`)
                }
            }
        },
    },
    plugins: [uuided(), versioned(), tracked(), softDeleted(), historical()],
    access: {
        read: access.canReadMeterReadings,
        create: access.canManageMeterReadings,
        update: access.canManageMeterReadings,
        delete: false,
        auth: true,
    },
})

module.exports = {
    MeterReading,
}