/**
 * Generated by `createschema meter.MeterReading 'number:Integer; date:DateTimeUtc; account?:Relationship:BillingAccount:SET_NULL; billingAccountMeter?:Relationship:BillingAccountMeter:SET_NULL; meter:Relationship:Meter:CASCADE; property:Relationship:Property:CASCADE; organization:Relationship:Organization:CASCADE; value:Integer; sectionName?:Text; floorName?:Text; unitName?:Text; client?:Relationship:User:SET_NULL; clientName?:Text; clientEmail?:Text; clientPhone?:Text; contact?:Relationship:Contact:SET_NULL; source:Relationship:MeterSource:PROTECT'`
 */
const dayjs = require('dayjs')
const get = require('lodash/get')
const isEmpty = require('lodash/isEmpty')
const isNil = require('lodash/isNil')

const conf = require('@open-condo/config')
const { GQLError, GQLErrorCode: { BAD_USER_INPUT } } = require('@open-condo/keystone/errors')
const { historical, versioned, uuided, tracked, softDeleted, dvAndSender, analytical } = require('@open-condo/keystone/plugins')
const { GQLListSchema, getById, find, getByCondition } = require('@open-condo/keystone/schema')
const { extractReqLocale } = require('@open-condo/locales/extractReqLocale')
const { i18n } = require('@open-condo/locales/loader')

const { CONTACT_FIELD, CLIENT_EMAIL_FIELD, CLIENT_NAME_FIELD, CLIENT_PHONE_LANDLINE_FIELD, CLIENT_FIELD } = require('@condo/domains/common/schema/fields')
const access = require('@condo/domains/meter/access/MeterReading')
const { METER_READING_MAX_VALUES_COUNT, METER_READING_BILLING_STATUSES, METER_READING_BILLING_STATUS_APPROVED } = require('@condo/domains/meter/constants/constants')
const { MOBILE_APP_SOURCE_ID } = require('@condo/domains/meter/constants/constants')
const {
    METER_READING_DATE_IN_FUTURE,
    METER_READING_FEW_VALUES,
    METER_READING_EXTRA_VALUES,
    BILLING_STATUS_MESSAGE_WITHOUT_BILLING_STATUS,
    METER_READING_DATE_AFTER_NOTIFY_END,
    METER_ARCHIVED,
    METER_VERIFICATION_MISSED,
} = require('@condo/domains/meter/constants/errors')
const { Meter } = require('@condo/domains/meter/utils/serverSchema')
const { connectContactToMeterReading } = require('@condo/domains/meter/utils/serverSchema/resolveHelpers')
const { addClientInfoToResidentMeterReading } = require('@condo/domains/meter/utils/serverSchema/resolveHelpers')
const { isReadingDateAllowed } = require('@condo/domains/meter/utils/serverSchema/resolveHelpers')
const { addOrganizationFieldPlugin } = require('@condo/domains/organization/schema/plugins/addOrganizationFieldPlugin')
const { RESIDENT } = require('@condo/domains/user/constants/common')

const ERRORS = {
    METER_READING_DATE_IN_FUTURE: {
        code: BAD_USER_INPUT,
        type: METER_READING_DATE_IN_FUTURE,
        message: 'Meter reading date can not be from the future',
        messageForUser: 'api.meter.meterReading.METER_READING_DATE_IN_FUTURE',
    },
    METER_READING_FEW_VALUES: {
        code: BAD_USER_INPUT,
        type: METER_READING_FEW_VALUES,
        message: 'Wrong values count: few values',
        messageForUser: 'api.meter.meterReading.METER_READING_FEW_VALUES',
    },
    METER_READING_EXTRA_VALUES: {
        code: BAD_USER_INPUT,
        type: METER_READING_EXTRA_VALUES,
        message: 'Wrong values count: extra values',
        messageForUser: 'api.meter.meterReading.METER_READING_EXTRA_VALUES',
    },
    BILLING_STATUS_MESSAGE_WITHOUT_BILLING_STATUS: {
        code: BAD_USER_INPUT,
        type: BILLING_STATUS_MESSAGE_WITHOUT_BILLING_STATUS,
        message: 'Can not set billingStatusText without billingStatus',
    },
    METER_READING_DATE_AFTER_NOTIFY_END: {
        code: BAD_USER_INPUT,
        type: METER_READING_DATE_AFTER_NOTIFY_END,
        message: 'Meter reading date cannot be after the end of the MeterReportingPeriod\'s notifyEndDay',
        messageForUser: 'api.meter.meterReading.METER_READING_DATE_AFTER_NOTIFY_END',
    },
    METER_ARCHIVED: {
        code: BAD_USER_INPUT,
        type: METER_ARCHIVED,
        message: 'Cannot pass meter readings for archived Meter',
        messageForUser: 'api.meter.meterReading.METER_ARCHIVED',
    },
    METER_VERIFICATION_MISSED: {
        code: BAD_USER_INPUT,
        type: METER_VERIFICATION_MISSED,
        message: 'Cannot pass meter readings for Meter whose next verification date is in the past',
        messageForUser: 'api.meter.meterReading.METER_VERIFICATION_MISSED',
    },
}

const MeterReading = new GQLListSchema('MeterReading', {
    schemaDoc: 'Meter reading taken from a client or billing',
    fields: {
        date: {
            schemaDoc: 'Date when the readings were taken',
            type: 'DateTimeUtc',
            hooks: {
                validateInput: async ({ context, resolvedData, fieldPath }) => {
                    const date = resolvedData[fieldPath]
                    const source = get(resolvedData, 'source')
                    if (date) {
                        const now = dayjs()
                        const readingDate = dayjs(date)
                        if (readingDate.isAfter(now)) {
                            throw new GQLError({
                                ...ERRORS.METER_READING_DATE_IN_FUTURE,
                                messageInterpolation: { givenDate: date },
                            }, context)
                        }

                        // NOTE: check against MeterReportingPeriod only readings passed from mobile app
                        if (source === MOBILE_APP_SOURCE_ID) {
                            const meter = await getByCondition('Meter', {
                                id: resolvedData['meter'],
                                deletedAt: null,
                            })

                            if (get(meter, 'archiveDate')) {
                                throw new GQLError(ERRORS.METER_ARCHIVED, context)
                            }

                            if (dayjs(get(meter, 'nextVerificationDate')).isBefore(now)) {
                                throw new GQLError(ERRORS.METER_VERIFICATION_MISSED, context)
                            }

                            const meterReportingPeriods = await find('MeterReportingPeriod', {
                                OR: [
                                    { AND: [{
                                        property: { AND: [{ id: meter.property }, { deletedAt: null } ] },
                                        deletedAt: null,
                                    }] },
                                    { AND: [{
                                        organization: { AND: [{ id: meter.organization }, { deletedAt: null } ] },
                                        property_is_null: true,
                                        deletedAt: null,
                                    }] },
                                ],
                            })

                            const meterReportingPeriod = meterReportingPeriods.find(period => period.property) ||
                                meterReportingPeriods.find(period => period.organization)

                            if (meterReportingPeriod) {
                                const { notifyEndDay } = meterReportingPeriod
                                if (!isReadingDateAllowed(date, meterReportingPeriod)) {
                                    throw new GQLError({
                                        ...ERRORS.METER_READING_DATE_AFTER_NOTIFY_END,
                                        messageInterpolation: { notifyEndDay, givenDate: date },
                                    }, context)
                                }
                            }
                        }
                    }
                },
            },
        },

        meter: {
            schemaDoc: 'Meter from which readings were taken',
            type: 'Relationship',
            ref: 'Meter',
            isRequired: true,
            knexOptions: { isNotNullable: true }, // Required relationship only!
            kmigratorOptions: { null: false, on_delete: 'models.CASCADE' },
        },

        value1: {
            schemaDoc: 'If the meter is single-tariff, then only this value will be filled in;' +
                'If multi-tariff, then the value of the first tariff will be in this field',
            type: 'Decimal',
        },

        value2: {
            schemaDoc: 'If the meter is multi-tariff, then the value of the second tariff is stored here',
            type: 'Decimal',
        },

        value3: {
            schemaDoc: 'If the meter is multi-tariff, then the value of the third tariff is stored here',
            type: 'Decimal',
        },

        value4: {
            schemaDoc: 'If the meter is multi-tariff, then the value of the fourth tariff is stored here',
            type: 'Decimal',
        },

        client: CLIENT_FIELD,
        contact: CONTACT_FIELD,
        clientName: CLIENT_NAME_FIELD,
        clientEmail: CLIENT_EMAIL_FIELD,
        clientPhone: CLIENT_PHONE_LANDLINE_FIELD,

        source: {
            schemaDoc: 'Meter reading source channel/system. Examples: call, mobile_app, billing, ...',
            type: 'Relationship',
            ref: 'MeterReadingSource',
            isRequired: true,
            knexOptions: { isNotNullable: true }, // Required relationship only!
            kmigratorOptions: { null: false, on_delete: 'models.PROTECT' },
        },

        billingStatus: {
            schemaDoc: 'A status from external billing system. Changing during processing the reading in external system. This field can be changed only by service user.',
            type: 'Select',
            dataType: 'string',
            options: METER_READING_BILLING_STATUSES,
            access: {
                read: true,
                create: access.canEditBillingStatusFields,
                update: access.canEditBillingStatusFields,
            },
        },
        billingStatusText: {
            schemaDoc: 'A message from external billing system. Set to null if billing status is `approved`. This field can be changed only by service user.',
            type: 'Text',
            hooks: {
                resolveInput: async ({ resolvedData, existingItem, fieldPath }) => {
                    const nextItem = { ...existingItem, ...resolvedData }
                    if (nextItem.billingStatus === METER_READING_BILLING_STATUS_APPROVED) {
                        return null
                    }

                    return resolvedData[fieldPath]
                },
            },
            access: {
                read: true,
                create: access.canEditBillingStatusFields,
                update: access.canEditBillingStatusFields,
            },
        },
        accountNumber: {
            schemaDoc: 'Account number of the related Meter at the moment of creation. Filled once.',
            type: 'Text',
            isRequired: false,
            access: {
                read: true,
                create: true,
                update: false,
            },
        },

    },
    hooks: {
        resolveInput: async ({ operation, context, resolvedData, existingItem }) => {
            // since request can come not only from transport layer - we have to use fallback auth detection
            const user = get(context, ['req', 'user']) || get(context, ['authedItem'])

            if (operation === 'create' && isEmpty(resolvedData['date'])) {
                resolvedData['date'] = new Date().toISOString()
            }

            if (operation === 'create' && user.type === RESIDENT) {
                addClientInfoToResidentMeterReading(context, resolvedData)
            }

            const meter = await Meter.getOne(context, {
                id: get(resolvedData, 'meter', null),
            }, 'organization { id } property { id } unitName unitType accountNumber')

            if (operation === 'create') {
                resolvedData['accountNumber'] = get(meter, 'accountNumber', null)
            }

            if (meter && resolvedData.clientName && resolvedData.clientPhone) {
                const contactCreationData = {
                    ...resolvedData,
                    organization: get(meter, ['organization', 'id']),
                    property: get(meter, ['property', 'id']),
                    unitName: get(meter, 'unitName'),
                    unitType: get(meter, 'unitType'),
                }

                resolvedData.contact = await connectContactToMeterReading(context, contactCreationData, existingItem)
            }

            return resolvedData
        },
        validateInput: async ({ context, resolvedData, existingItem }) => {
            const newItem = { ...existingItem, ...resolvedData }
            const locale = extractReqLocale(context.req) || conf.DEFAULT_LOCALE

            if (!isNil(newItem.billingStatusText) && newItem.billingStatus === null) {
                throw new GQLError(ERRORS.BILLING_STATUS_MESSAGE_WITHOUT_BILLING_STATUS, context)
            }

            const meterId = get(newItem, 'meter')

            const meter = await getById('Meter', meterId)

            const emptyFieldsNames = []
            for (let i = 1; i <= meter.numberOfTariffs; i++) {
                if (isNil(get(newItem, `value${i}`))) {
                    emptyFieldsNames.push(`value${i}`)
                }
            }
            if (emptyFieldsNames.length > 0) {
                const localizedFieldsNames = emptyFieldsNames.map((fieldName) => i18n(`meter.import.column.${fieldName}`, { locale }))
                throw new GQLError({
                    ...ERRORS.METER_READING_FEW_VALUES,
                    messageInterpolation: {
                        meterNumber: meter.number,
                        numberOfTariffs: meter.numberOfTariffs,
                        fieldsNames: localizedFieldsNames.join(', '),
                    },
                }, context)
            }

            const extraFieldsNames = []
            for (let i = meter.numberOfTariffs + 1; i <= METER_READING_MAX_VALUES_COUNT; i++) {
                const value = get(newItem, `value${i}`)
                if (!isNil(value) && !isEmpty(value)) {
                    extraFieldsNames.push(`value${i}`)
                }
            }
            if (extraFieldsNames.length > 0) {
                const localizedFieldsNames = extraFieldsNames.map((fieldName) => i18n(`meter.import.column.${fieldName}`, { locale }))
                throw new GQLError({
                    ...ERRORS.METER_READING_EXTRA_VALUES,
                    messageInterpolation: {
                        meterNumber: meter.number,
                        numberOfTariffs: meter.numberOfTariffs,
                        fieldsNames: localizedFieldsNames.join(', '),
                    },
                }, context)
            }
        },
    },
    plugins: [
        addOrganizationFieldPlugin({ fromField: 'meter', isRequired: true }),
        uuided(),
        versioned(),
        tracked(),
        softDeleted(),
        dvAndSender(),
        historical(),
        analytical(),
    ],
    access: {
        read: access.canReadMeterReadings,
        create: access.canManageMeterReadings,
        update: access.canManageMeterReadings,
        delete: false,
        auth: true,
    },
})

module.exports = {
    MeterReading,
}
