/**
 * Generated by `createschema miniapp.DescriptionBlock 'description:Text; image:File;'`
 */

const { throwAuthenticationError } = require('@condo/domains/common/utils/apolloErrorFormatter')

// Anybody should be able to see this blocks
async function canReadDescriptionBlocks ({ authentication: { item: user } }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin) return {}

    return {}
}

// Can be managed by support for now, someday third-party developers will have access here too
async function canManageDescriptionBlocks ({ authentication: { item: user } }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    return !!(user.isAdmin || user.isSupport)
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadDescriptionBlocks,
    canManageDescriptionBlocks,
}
