/**
 * Generated by `createschema news.NewsItemFile 'newsItem:Relationship:NewsItem:CASCADE; file:File;'`
 */

const FileAdapter = require('@open-condo/keystone/fileAdapter/fileAdapter')
const { getFileMetaAfterChange } = require('@open-condo/keystone/fileAdapter/fileAdapter')
const { historical, versioned, uuided, tracked, softDeleted, dvAndSender, analytical } = require('@open-condo/keystone/plugins')
const { GQLListSchema } = require('@open-condo/keystone/schema')

const access = require('@condo/domains/news/access/NewsItemFile')
const { addOrganizationFieldPlugin } = require('@condo/domains/organization/schema/plugins/addOrganizationFieldPlugin')


const DOCUMENT_FOLDER_NAME = 'NewsItemFile'
const Adapter = new FileAdapter(DOCUMENT_FOLDER_NAME, false)
const fileMetaAfterChange = getFileMetaAfterChange(Adapter)

const NewsItemFile = new GQLListSchema('NewsItemFile', {
    schemaDoc: 'A link between a NewsItem and an uploaded file. Represents attachments (images, documents, etc.) related to a news item',
    fields: {

        newsItem: {
            schemaDoc: 'The news item this file belongs to',
            type: 'Relationship',
            ref: 'NewsItem',
            isRequired: true,
            knexOptions: { isNotNullable: true }, // Required relationship only!
            kmigratorOptions: { null: false, on_delete: 'models.CASCADE' },
        },

        file: {
            schemaDoc: 'The uploaded file attached to the news item. Stores file metadata and storage reference',
            type: 'File',
            adapter: Adapter,
            isRequired: true,
        },

    },

    hooks: {
        resolveInput: ({ resolvedData, operation, context, listKey }) => {
            // if (operation === 'create' && !resolvedData['name']) {
            //     resolvedData['name'] = resolvedData?.file?.originalFilename || context?._fileNewFlow?.[`${listKey}.file`]?.originalFilename
            // }

            return resolvedData
        },
        afterChange: fileMetaAfterChange,
        afterDelete: async ({ existingItem }) => {
            if (existingItem.file) {
                await Adapter.delete(existingItem.file)
            }
        },
    },

    plugins: [
        addOrganizationFieldPlugin({ fromField: 'newsItem' }),
        uuided(),
        versioned(),
        tracked(),
        softDeleted(),
        dvAndSender(),
        historical(),
        analytical(),
    ],
    access: {
        read: access.canReadNewsItemFiles,
        create: access.canManageNewsItemFiles,
        update: access.canManageNewsItemFiles,
        delete: false,
        auth: true,
    },
})

module.exports = {
    NewsItemFile,
}
