/**
 * Generated by `createschema news.NewsItemSharing 'b2bApp:Relationship:B2BApp:CASCADE; newsItem:Relationship:NewsItem:CASCADE; sharingParams:Json; status:Select:processing,published,moderation,declined,archive; statusMessage:Text; lastGetRecipientsRequest:Json; lastPostRequest:Json; lastGetStatusRequest:Json; publicationViewsCount:Integer;'`
 */

const { Text, Relationship, Integer, Select, Checkbox, DateTimeUtc, CalendarDay, Decimal, Password, File, Url } = require('@keystonejs/fields')
const get = require('lodash/get')

const { Json } = require('@open-condo/keystone/fields')
const { historical, versioned, uuided, tracked, softDeleted, dvAndSender } = require('@open-condo/keystone/plugins')
const { GQLListSchema, find, getById } = require('@open-condo/keystone/schema')

const access = require('@condo/domains/news/access/NewsItemSharing')

const { normalizeText } = require('../../common/utils/text')


const NewsItemSharing = new GQLListSchema('NewsItemSharing', {
    schemaDoc: 'Existence of this models means that certain NewsItem should published in certain B2BApp that implements NewsSharing API.',
    fields: {

        b2bAppContext: {
            schemaDoc: 'Connection to the mini-app responsible for sending news to the selected resource',
            type: Relationship,
            ref: 'B2BAppContext',
            isRequired: true,
            knexOptions: { isNotNullable: true }, // Required relationship only!
            kmigratorOptions: { null: false, on_delete: 'models.CASCADE' },
        },

        newsItem: {
            schemaDoc: 'Connection to the published news item',
            type: Relationship,
            ref: 'NewsItem',
            isRequired: true,
            knexOptions: { isNotNullable: true }, // Required relationship only!
            kmigratorOptions: { null: false, on_delete: 'models.CASCADE' },
        },

        sharingParams: {
            schemaDoc: 'Sending parameters specific to a particular mini-app',
            type: Json,
            isRequired: false,
        },

        status: {
            schemaDoc: 'Publication status of the news: updated automatically',
            type: Select,
            options: 'scheduled,processing,published,moderation,declined,archive',
            isRequired: true,
            defaultValue: 'scheduled',
        },

        statusMessage: {
            schemaDoc: 'Explanations regarding the publication status. For example, the reason for rejection by moderators, details of a system error on the mini-app side',
            type: Text,
            isRequired: false,
        },

        lastGetRecipientsRequest: {
            schemaDoc: 'The outcome from the most recent invocation of the lastGetRecipientsRequest',
            type: Json,
            isRequired: false,
        },

        lastPostRequest: {
            schemaDoc: 'The outcome from the most recent invocation of the lastPostRequest',
            type: Json,
            isRequired: false,
        },

        lastGetStatusRequest: {
            schemaDoc: 'The outcome from the most recent invocation of the lastGetStatusRequest',
            type: Json,
            isRequired: false,
        },

        publicationViewsCount: {
            schemaDoc: 'How many users have seen this shared news item',
            type: Integer,
            isRequired: true,
            defaultValue: 0,
        },
    },

    hooks: {
        afterChange: async ({ operation, updatedItem, existingData }) => {
            const b2bAppContextId = get( updatedItem, ['b2bAppContext'])
            const b2bAppContext = await getById('B2BAppContext', b2bAppContextId)

            const newsItemId = get( updatedItem, ['newsItem'])
            const newsItem = await getById('NewsItem', newsItemId)

            const postUrl = get(b2bAppContext, ['settings', 'postUrl'])

            const chatId = get(b2bAppContext, ['settings', 'chatId'])
            const title = get(newsItem, 'title')
            const message = get(newsItem, 'body')

            try {
                const response = await fetch(postUrl, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        title,
                        message,
                        chatId,
                    }),
                })
                console.log(response)
            } catch (err) {
                console.log(err)
            }
        },
    },

    plugins: [uuided(), versioned(), tracked(), softDeleted(), dvAndSender(), historical()],
    access: {
        read: access.canReadNewsItemSharings,
        create: access.canManageNewsItemSharings,
        update: access.canManageNewsItemSharings,
        delete: false,
        auth: true,
    },
})

module.exports = {
    NewsItemSharing,
}
