/**
 * Generated by `createschema onboarding.UserHelpRequest 'type:Select:callback;importFile;user:Relationship:User:CASCADE;organization:Relationship:Organization:CASCADE;phone:Text;file?:File;meta?:Json'`
 */

const get = require('lodash/get')
const isEmpty = require('lodash/isEmpty')
const omit = require('lodash/omit')

const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')
const { getById } = require('@open-condo/keystone/schema')

const { ACTIVATE_SUBSCRIPTION_TYPE } = require('@condo/domains/onboarding/constants/userHelpRequest')
const { checkUserEmploymentInOrganizations, getEmployedOrganizationsByPermissions } = require('@condo/domains/organization/utils/accessSchema')
const { STAFF } = require('@condo/domains/user/constants/common')


const AVAILABLE_TO_UPDATE_USER_HELP_REQUEST_FIELDS = ['dv', 'sender', 'isReadyToSend']

async function canReadUserHelpRequests ({ authentication: { item: user }, context }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    if (user.isAdmin || user.isSupport) return {}

    if (user.type !== STAFF) {
        return { createdBy: { id: user.id } }
    }

    const employedOrganizationIds = await getEmployedOrganizationsByPermissions(context, user, [])
    if (employedOrganizationIds.length > 0) {
        return {
            OR: [
                { createdBy: { id: user.id } },
                {
                    AND: [
                        { type: ACTIVATE_SUBSCRIPTION_TYPE },
                        { organization: { id_in: employedOrganizationIds } },
                    ],
                },
            ],
        }
    }

    return { createdBy: { id: user.id } }
}

async function canManageUserHelpRequests ({ authentication: { item: user }, context, originalInput, operation, itemId }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin) return true

    if (user.type !== STAFF) return false

    if (operation === 'create') {
        const organizationIdInRequest = get(originalInput, 'organization.connect.id', null)

        if (!organizationIdInRequest) return false

        return await checkUserEmploymentInOrganizations(context, user, organizationIdInRequest)
    } else if (operation === 'update') {
        const helpRequest = await getById('UserHelpRequest', itemId)
        const inaccessibleUpdatedFields = omit(originalInput, AVAILABLE_TO_UPDATE_USER_HELP_REQUEST_FIELDS)

        // can update only isReadyToSend field from false to true
        if (
            helpRequest.createdBy !== user.id ||
            !isEmpty(inaccessibleUpdatedFields) ||
            (helpRequest.isReadyToSend && originalInput.isReadyToSend === false)
        ) return false

        return true
    }

    return false
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadUserHelpRequests,
    canManageUserHelpRequests,
}
