/**
 * Generated by `createschema onboarding.OnBoarding 'completed:Checkbox; stepsTransitions:Json;'`
 */

const { makeClient, UUID_RE, DATETIME_RE } = require('@core/keystone/test.utils')

const { OnBoarding, createTestOnBoarding, updateTestOnBoarding } = require('@condo/domains/onboarding/utils/testSchema')
const { expectToThrowAuthenticationErrorToObj, expectToThrowAuthenticationErrorToObjects } = require('@condo/domains/common/utils/testSchema')
const { makeClientWithNewRegisteredAndLoggedInUser } = require('@condo/domains/user/utils/testSchema')
const { expectToThrowAccessDeniedErrorToObj } = require('../../common/utils/testSchema')

describe('OnBoarding', () => {
    test('user: create OnBoarding', async () => {
        const client = await makeClientWithNewRegisteredAndLoggedInUser()

        const [obj, attrs] = await createTestOnBoarding(
            client,
            {
                type: 'ADMINISTRATOR',
                user: { connect: { id: client.user.id } },
                stepsTransitions: {},
            },
        )
        expect(obj.id).toMatch(UUID_RE)
        expect(obj.dv).toEqual(1)
        expect(obj.sender).toEqual(attrs.sender)
        expect(obj.v).toEqual(1)
        expect(obj.newId).toEqual(null)
        expect(obj.deletedAt).toEqual(null)
        expect(obj.createdBy).toEqual(expect.objectContaining({ id: client.user.id }))
        expect(obj.updatedBy).toEqual(expect.objectContaining({ id: client.user.id }))
        expect(obj.createdAt).toMatch(DATETIME_RE)
        expect(obj.updatedAt).toMatch(DATETIME_RE)
        expect(obj.type).toMatch('ADMINISTRATOR')
        expect(obj.user).toEqual(expect.objectContaining({ id: client.user.id }))
        expect(obj.stepsTransitions).toEqual({})
    })

    test('anonymous: create OnBoarding', async () => {
        const client = await makeClient()
        await expectToThrowAuthenticationErrorToObj(async () => {
            await createTestOnBoarding(client)
        })
    })

    test('user: read OnBoarding', async () => {
        const client = await makeClientWithNewRegisteredAndLoggedInUser()
        const [obj, attrs] = await createTestOnBoarding(client, {
            type: 'ADMINISTRATOR',
            user: { connect: { id: client.user.id } },
            stepsTransitions: {},
        })

        const objs = await OnBoarding.getAll(client, {}, { sortBy: ['updatedAt_DESC'] })
        expect(objs).toHaveLength(1)
        expect(objs[0]).toMatchObject({
            id: obj.id,
            sender: attrs.sender,
            createdBy: expect.objectContaining({ id: client.user.id }),
            updatedBy: expect.objectContaining({ id: client.user.id }),
            createdAt: obj.createdAt,
            updatedAt: obj.updatedAt,
            type: 'ADMINISTRATOR',
            user: expect.objectContaining({ id: client.user.id }),
            stepsTransitions: attrs.stepsTransitions,
        })
    })

    test('anonymous: read OnBoarding', async () => {
        const client = await makeClient()

        await expectToThrowAuthenticationErrorToObjects(async () => {
            await OnBoarding.getAll(client)
        })
    })

    test('user: update OnBoarding', async () => {
        const client = await makeClientWithNewRegisteredAndLoggedInUser()
        const [objCreated] = await createTestOnBoarding(client, {
            type: 'ADMINISTRATOR',
            user: { connect: { id: client.user.id } },
            stepsTransitions: {},
        })

        const payload = { completed: true }
        const [objUpdated, attrs] = await updateTestOnBoarding(client, objCreated.id, payload)

        expect(objUpdated.id).toEqual(objCreated.id)
        expect(objUpdated.dv).toEqual(1)
        expect(objUpdated.sender).toEqual(attrs.sender)
        expect(objUpdated.v).toEqual(2)
        expect(objUpdated.newId).toEqual(null)
        expect(objUpdated.completed).toEqual(true)
        expect(objUpdated.deletedAt).toEqual(null)
        expect(objUpdated.createdBy).toEqual(expect.objectContaining({ id: client.user.id }))
        expect(objUpdated.updatedBy).toEqual(expect.objectContaining({ id: client.user.id }))
        expect(objUpdated.createdAt).toMatch(DATETIME_RE)
        expect(objUpdated.updatedAt).toMatch(DATETIME_RE)
        expect(objUpdated.updatedAt).not.toEqual(objUpdated.createdAt)
        expect(objUpdated.completed).toEqual(true)
    })

    test('user: do not have access to another user onbording', async () => {
        const client1 = await makeClientWithNewRegisteredAndLoggedInUser()
        const [obj] = await createTestOnBoarding(client1, {
            type: 'ADMINISTRATOR',
            user: { connect: { id: client1.user.id } },
            stepsTransitions: {},
        })

        const client2 = await makeClientWithNewRegisteredAndLoggedInUser()
        const payload = { completed: true }
        await expectToThrowAccessDeniedErrorToObj(async () => await updateTestOnBoarding(client2, obj.id, payload))
    })
})
