/**
 * Generated by `createschema onboarding.OnBoardingStep 'icon:Text; title:Text; description:Text; action:Select:create,read,update,delete; entity:Text; onBoarding:Relationship:OnBoarding:SET_NULL;'`
 */
const { makeClient, UUID_RE, DATETIME_RE } = require('@open-condo/keystone/test.utils')
const { expectToThrowAuthenticationErrorToObjects, expectToThrowAuthenticationErrorToObj, expectToThrowAccessDeniedErrorToObj } = require('@open-condo/keystone/test.utils')

const { OnBoardingStep, createTestOnBoardingStep, createTestOnBoarding, updateTestOnBoardingStep } = require('@condo/domains/onboarding/utils/testSchema')
const { makeClientWithNewRegisteredAndLoggedInUser } = require('@condo/domains/user/utils/testSchema')

describe('OnBoardingStep', () => {
    afterAll( () => {
        if (global.gc) {
            global.gc()
        }
    })
    test('user: create OnBoardingStep', async () => {
        const client = await makeClientWithNewRegisteredAndLoggedInUser()

        const [onboarding] = await createTestOnBoarding(client)

        const [obj] = await createTestOnBoardingStep(client, onboarding, {
            icon: 'organization',
            required: true,
            title: 'title',
            description: 'description',
            action: 'create',
            entity: 'Organization',
            order: 1,
        })

        expect(obj.id).toMatch(UUID_RE)
        expect(obj.createdAt).toMatch(DATETIME_RE)
        expect(obj.updatedAt).toMatch(DATETIME_RE)
        expect(obj).toMatchObject({
            icon: 'organization',
            required: true,
            title: 'title',
            description: 'description',
            titleNonLocalized: 'title',
            descriptionNonLocalized: 'description',
            action: 'create',
            entity: 'Organization',
            order: 1,
            v: 1,
            dv: 1,
            newId: null,
            deletedAt: null,
            createdBy: expect.objectContaining({ id: client.user.id }),
            updatedBy: expect.objectContaining({ id: client.user.id }),
        })
    })

    test('user: read OnBoardingStep', async () => {
        const client = await makeClientWithNewRegisteredAndLoggedInUser()
        const [onboarding] = await createTestOnBoarding(client)

        const [obj] = await createTestOnBoardingStep(client, onboarding)

        const objs = await OnBoardingStep.getAll(client, {}, { sortBy: ['updatedAt_DESC'] })

        expect(objs).toHaveLength(1)
        expect(objs[0]).toMatchObject({
            id: obj.id,
        })
    })

    test('anonymous: read OnBoardingStep', async () => {
        const client = await makeClient()

        await expectToThrowAuthenticationErrorToObjects(async () => {
            await OnBoardingStep.getAll(client)
        })
    })

    test('user: update OnBoardingStep', async () => {
        const client = await makeClientWithNewRegisteredAndLoggedInUser()
        const [onboarding] = await createTestOnBoarding(client)
        const [objCreated] = await createTestOnBoardingStep(client, onboarding)

        const payload = { completed: true }
        const [objUpdated, attrs] = await updateTestOnBoardingStep(client, objCreated.id, payload)

        expect(objUpdated.id).toEqual(objCreated.id)
        expect(objUpdated.dv).toEqual(1)
        expect(objUpdated.sender).toEqual(attrs.sender)
        expect(objUpdated.completed).toEqual(true)
        expect(objUpdated.v).toEqual(2)
        expect(objUpdated.newId).toEqual(null)
        expect(objUpdated.deletedAt).toEqual(null)
        expect(objUpdated.createdBy).toEqual(expect.objectContaining({ id: client.user.id }))
        expect(objUpdated.updatedBy).toEqual(expect.objectContaining({ id: client.user.id }))
        expect(objUpdated.createdAt).toMatch(DATETIME_RE)
        expect(objUpdated.updatedAt).toMatch(DATETIME_RE)
        expect(objUpdated.updatedAt).not.toEqual(objUpdated.createdAt)
    })

    test('anonymous: update OnBoardingStep', async () => {
        const client = await makeClientWithNewRegisteredAndLoggedInUser()
        const [onboarding] = await createTestOnBoarding(client)
        const [objCreated] = await createTestOnBoardingStep(client, onboarding)

        const anonymus = await makeClient()
        const payload = { completed: true }
        await expectToThrowAuthenticationErrorToObj(async () => {
            await updateTestOnBoardingStep(anonymus, objCreated.id, payload)
        })
    })

    test('user: do not have access to another user OnBordingStep', async () => {
        const client1 = await makeClientWithNewRegisteredAndLoggedInUser()
        const [onboarding] = await createTestOnBoarding(client1, {
            type: 'ADMINISTRATOR',
            user: { connect: { id: client1.user.id } },
            stepsTransitions: {},
        })
        const [obj] = await createTestOnBoardingStep(client1, onboarding)

        const client2 = await makeClientWithNewRegisteredAndLoggedInUser()
        const payload = { completed: true }
        await expectToThrowAccessDeniedErrorToObj(async () => await updateTestOnBoardingStep(client2, obj.id, payload))
    })
})
