/**
 * Generated by `createschema organization.OrganizationEmployeeSpecialization 'employee:Relationship:OrganizationEmployee:CASCADE; specialization:Relationship:TicketCategoryClassifier:CASCADE;'`
 */

const get = require('lodash/get')

const { isSoftDelete } = require('@open-condo/keystone/access')
const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')
const { getById } = require('@open-condo/keystone/schema')

const {
    getEmployedOrRelatedOrganizationsByPermissions,
    checkPermissionsInEmployedOrganizations,
} = require('@condo/domains/organization/utils/accessSchema')

async function canReadOrganizationEmployeeSpecializations ({ authentication: { item: user }, context }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    if (user.isAdmin || user.isSupport) return {}

    const permittedOrganizations = await getEmployedOrRelatedOrganizationsByPermissions(context, user, 'canReadEmployees')

    return {
        employee: {
            organization: {
                id_in: permittedOrganizations,
            },
        },
    }
}

async function canManageOrganizationEmployeeSpecializations ({ authentication: { item: user }, context, originalInput, operation, itemId }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin || user.isSupport) return true

    if (operation === 'create') {
        const employeeId = get(originalInput, ['employee', 'connect', 'id'])
        if (!employeeId) return false

        const employee = await getById('OrganizationEmployee', employeeId)
        const organizationId = get(employee, 'organization')

        if (!organizationId) return false

        return await checkPermissionsInEmployedOrganizations(context, user, organizationId, 'canManageEmployees')
    } else if (operation === 'update' && itemId) {
        if (!isSoftDelete(originalInput)) return false

        const organizationEmployeeSpecialization = await getById('OrganizationEmployeeSpecialization', itemId)
        if (!organizationEmployeeSpecialization) return false

        const employeeId = organizationEmployeeSpecialization.employee
        const employee = await getById('OrganizationEmployee', employeeId)
        const organizationId = get(employee, 'organization')

        if (!organizationId) return false

        return await checkPermissionsInEmployedOrganizations(context, user, organizationId, 'canManageEmployees')
    }

    return false
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadOrganizationEmployeeSpecializations,
    canManageOrganizationEmployeeSpecializations,
}
