/**
 * Generated by `createservice property.ExportPropertiesToExcelService --type queries`
 */
const { throwAuthenticationError } = require('@condo/domains/common/utils/apolloErrorFormatter')
const { Organization: OrganizationAPI } = require('@condo/domains/organization/utils/serverSchema')
const { checkOrganizationPermission, checkRelatedOrganizationPermission } = require('@condo/domains/organization/utils/accessSchema')
const get = require('lodash/get')


async function canExportPropertiesToExcel ({ args: { data: { where } }, authentication: { item: user }, context }) {
    if (!user) return throwAuthenticationError()
    if (user.isAdmin) return true
    const organizationId = get(where, 'organization.id')
    if (!organizationId) {
        const organizationFromWhere = get(where, 'organization')
        const [relatedFromOrganization] = await OrganizationAPI.getAll(context, organizationFromWhere)
        if (!relatedFromOrganization) {
            return false
        }
        const canManageRelatedOrganizationProperties = await checkRelatedOrganizationPermission(user.id, relatedFromOrganization.id, 'canManageProperties')
        return canManageRelatedOrganizationProperties
    }
    return await checkOrganizationPermission(user.id, organizationId, 'canManageProperties')
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canExportPropertiesToExcel,
}