/**
 * Generated by `createschema property.Property 'organization:Text; name:Text; address:Text; addressMeta:Json; type:Select:building,village; map?:Json'`
 */

async function canReadProperties ({ authentication: { item: user } }) {
    if (!user) return false
    if (user.isAdmin) return {}

    return {
        OR: [
            { organization: { employees_some: { user: { id: user.id } } } },
        ],
    }
}

async function canManageProperties ({ authentication: { item: user }, operation }) {
    if (!user) return false
    if (user.isAdmin) return true
    if (operation === 'create') {
        if (user.isPhoneVerified) {
            return true
        }
    } else if (operation === 'update') {
        if (user.isPhoneVerified) {
            return true
        }
    }
    return false
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadProperties,
    canManageProperties,
}
