/**
 * Generated by `createschema resident.ServiceConsumer 'resident:Relationship:Resident:CASCADE; billingAccount?:Relationship:BillingAccount:SET_NULL; accountNumber:Text;'`
 */

const { Resident } = require('../utils/serverSchema')
const { Text, Relationship } = require('@keystonejs/fields')
const { GQLListSchema } = require('@core/keystone/schema')
const { historical, versioned, uuided, tracked, softDeleted } = require('@core/keystone/plugins')
const { SENDER_FIELD, DV_FIELD } = require('@condo/domains/common/schema/fields')
const access = require('@condo/domains/resident/access/ServiceConsumer')
const { BillingAccount } = require('@condo/domains/billing/utils/serverSchema')


const ServiceConsumer = new GQLListSchema('ServiceConsumer', {
    schemaDoc: 'Service Consumer object. Existence of this object means that the resident is willing to pay for certain services',
    fields: {
        dv: DV_FIELD,
        sender: SENDER_FIELD,

        resident: {
            schemaDoc: 'Resident object',
            type: Relationship,
            ref: 'Resident',
            isRequired: true,
            knexOptions: { isNotNullable: true }, // Required relationship only!
            kmigratorOptions: { null: false, on_delete: 'models.CASCADE' },
        },

        billingAccount: {
            schemaDoc: 'System-wide billing account, that will allow to pay for all services from all organizations',
            type: Relationship,
            ref: 'BillingAccount',
            kmigratorOptions: { null: true, on_delete: 'models.SET_NULL' },
        },

        accountNumber: {
            schemaDoc: 'Account number from resident',
            type: Text,
            isRequired: true,
        },
    },
    plugins: [uuided(), versioned(), tracked(), softDeleted(), historical()],
    access: {
        read: access.canReadServiceConsumers,
        create: access.canManageServiceConsumers,
        update: access.canManageServiceConsumers,
        delete: false,
        auth: true,
    },
    hooks: {
        beforeChange: async ({ context, resolvedData }) => {
            const { resident, accountNumber } = resolvedData
            if (resolvedData.billingAccount) {
                return
            }
            const [ residentAttrs ] = await Resident.getAll(context, { id: resident })
            const applicableBillingAccounts = await BillingAccount.getAll(
                context,
                {
                    OR: [
                        {
                            number: accountNumber,
                            unitName: residentAttrs.unitName,
                        },
                        {
                            globalId: accountNumber,
                            unitName: residentAttrs.unitName,
                        },
                    ],
                }
            )
            if (applicableBillingAccounts.length >= 1) {
                resolvedData.billingAccount = applicableBillingAccounts[0].id
            }
        },
    },
})

module.exports = {
    ServiceConsumer,
}
