/**
 * Generated by `createschema subscription.SubscriptionContext 'organization:Relationship:Organization:CASCADE; subscriptionPlan:Relationship:SubscriptionPlan:PROTECT; startAt:DateTimeUtc; endAt:DateTimeUtc; basePrice:Decimal; calculatedPrice:Decimal; appliedRules:Json; manualPrice:Decimal; manualPriceReason:Text; isTrial:Checkbox;'`
 */

const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')

const { getEmployedOrRelatedOrganizationsByPermissions } = require('@condo/domains/organization/utils/accessSchema')

async function canReadSubscriptionContexts ({ authentication: { item: user }, context }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    if (user.isAdmin || user.isSupport) return {}

    // Employee can read their organization's subscription contexts
    const permittedOrganizations = await getEmployedOrRelatedOrganizationsByPermissions(context, user, [])
    
    return {
        organization: { id_in: permittedOrganizations },
    }
}

async function canManageSubscriptionContexts ({ authentication: { item: user }, context, operation, originalInput, itemId }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    if (user.isAdmin || user.isSupport) return true

    // For update operations, allow employees with canManageSubscriptions to update paymentMethod field only
    if (operation === 'update') {
        const updatedFields = Object.keys(originalInput || {}).filter(key => !['dv', 'sender'].includes(key))
        const isOnlyPaymentMethodUpdate = updatedFields.length === 1 && updatedFields[0] === 'paymentMethod'
        
        if (isOnlyPaymentMethodUpdate && itemId) {
            const permittedOrganizations = await getEmployedOrRelatedOrganizationsByPermissions(context, user, 'canManageSubscriptions')
            
            return {
                organization: { id_in: permittedOrganizations },
            }
        }
    }

    return false
}

module.exports = {
    canReadSubscriptionContexts,
    canManageSubscriptionContexts,
}
