/**
 * Generated by `createschema subscription.SubscriptionContext 'organization:Relationship:Organization:CASCADE; subscriptionPlan:Relationship:SubscriptionPlan:PROTECT; startAt:DateTimeUtc; endAt:DateTimeUtc; basePrice:Decimal; calculatedPrice:Decimal; appliedRules:Json; isTrial:Checkbox;'`
 */

const { GQLError, GQLErrorCode: { BAD_USER_INPUT } } = require('@open-condo/keystone/errors')
const { historical, versioned, uuided, tracked, softDeleted, dvAndSender } = require('@open-condo/keystone/plugins')
const { GQLListSchema, getById, find } = require('@open-condo/keystone/schema')

const { ACTIVATE_SUBSCRIPTION_TYPE } = require('@condo/domains/onboarding/constants/userHelpRequest')
const { UserHelpRequest } = require('@condo/domains/onboarding/utils/serverSchema')
const { ORGANIZATION_OWNED_FIELD } = require('@condo/domains/organization/schema/fields')
const access = require('@condo/domains/subscription/access/SubscriptionContext')
const { freezePricingRule } = require('@condo/domains/subscription/utils/subscriptionFridge')

const ERRORS = {
    END_DATE_MUST_BE_AFTER_START_DATE: {
        code: BAD_USER_INPUT,
        type: 'END_DATE_MUST_BE_AFTER_START_DATE',
        message: 'endAt must be after startAt',
    },
    PRICING_RULE_REQUIRED_FOR_NON_TRIAL: {
        code: BAD_USER_INPUT,
        type: 'PRICING_RULE_REQUIRED_FOR_NON_TRIAL',
        message: 'pricingRule is required for non-trial subscriptions',
    },
    PRICING_RULE_PLAN_MISMATCH: {
        code: BAD_USER_INPUT,
        type: 'PRICING_RULE_PLAN_MISMATCH',
        message: 'pricingRule must belong to the same subscriptionPlan',
    },
    PRICING_RULE_NOT_ALLOWED_FOR_TRIAL: {
        code: BAD_USER_INPUT,
        type: 'PRICING_RULE_NOT_ALLOWED_FOR_TRIAL',
        message: 'pricingRule cannot be specified for trial subscriptions',
    },
}


const SubscriptionContext = new GQLListSchema('SubscriptionContext', {
    schemaDoc: 'Subscription context linking organization to a subscription plan with calculated pricing. When changing subscription plan, a new context must be created',
    fields: {

        organization: {
            ...ORGANIZATION_OWNED_FIELD,
            schemaDoc: 'Organization that has this subscription',
        },

        subscriptionPlan: {
            schemaDoc: 'Subscription plan selected by the organization',
            type: 'Relationship',
            ref: 'SubscriptionPlan',
            isRequired: true,
            knexOptions: { isNotNullable: true },
            kmigratorOptions: { null: false, on_delete: 'models.PROTECT' },
            access: {
                read: true,
                create: true,
                update: false,
            },
        },

        startAt: {
            schemaDoc: 'Subscription start date',
            type: 'DateTimeUtc',
            isRequired: true,
            access: {
                read: true,
                create: true,
                update: false,
            },
        },

        endAt: {
            schemaDoc: 'Subscription end date. If null, subscription is unlimited',
            type: 'DateTimeUtc',
            isRequired: false,
            access: {
                read: true,
                create: true,
                update: false,
            },
        },

        pricingRule: {
            schemaDoc: 'Pricing rule that defines the subscription price. Required for non-trial subscriptions. Must belong to the same subscriptionPlan',
            type: 'Relationship',
            ref: 'SubscriptionPlanPricingRule',
            isRequired: false,
            kmigratorOptions: { null: true, on_delete: 'models.PROTECT' },
            access: {
                read: true,
                create: true,
                update: false,
            },
        },

        frozenPricingRule: {
            schemaDoc: 'Frozen snapshot of the pricing rule at the moment of subscription creation. Stored for audit purposes',
            type: 'Json',
            isRequired: false,
            defaultValue: null,
            access: {
                read: true,
                create: false,
                update: false,
            },
        },

        isTrial: {
            schemaDoc: 'Whether this is a trial subscription. Trial subscriptions provide temporary free access to the plan features for a limited period (defined by SubscriptionPlan.trialDays)',
            type: 'Checkbox',
            defaultValue: false,
            isRequired: true,
            access: {
                read: true,
                create: true,
                update: false,
            },
        },

        daysRemaining: {
            schemaDoc: 'Number of days remaining in the subscription. Calculated from server time. Returns 0 if subscription has expired',
            type: 'Virtual',
            graphQLReturnType: 'Int',
            resolver: (item) => {
                if (!item.endAt) return null

                const now = new Date()
                const endAt = new Date(item.endAt)
                const diffMs = endAt.getTime() - now.getTime()

                if (diffMs <= 0) return 0

                return Math.ceil(diffMs / (1000 * 60 * 60 * 24))
            },
        },

    },
    hooks: {
        validateInput: async ({ resolvedData, existingItem, operation, context }) => {
            const startAt = resolvedData.startAt || existingItem?.startAt
            const endAt = resolvedData.endAt || existingItem?.endAt

            if (startAt && endAt && new Date(startAt) >= new Date(endAt)) {
                throw new GQLError(ERRORS.END_DATE_MUST_BE_AFTER_START_DATE, context)
            }

            if (operation === 'create') {
                const isTrial = resolvedData.isTrial
                const pricingRuleId = resolvedData.pricingRule

                // pricingRule is required for non-trial subscriptions
                if (!isTrial && !pricingRuleId) {
                    throw new GQLError(ERRORS.PRICING_RULE_REQUIRED_FOR_NON_TRIAL, context)
                }

                // pricingRule is not allowed for trial subscriptions
                if (isTrial && pricingRuleId) {
                    throw new GQLError(ERRORS.PRICING_RULE_NOT_ALLOWED_FOR_TRIAL, context)
                }

                // If pricingRule is provided, validate it belongs to the same plan
                if (pricingRuleId) {
                    const pricingRule = await getById('SubscriptionPlanPricingRule', pricingRuleId)
                    const subscriptionPlanId = resolvedData.subscriptionPlan

                    if (pricingRule.subscriptionPlan !== subscriptionPlanId) {
                        throw new GQLError(ERRORS.PRICING_RULE_PLAN_MISMATCH, context)
                    }
                }
            }
        },
        resolveInput: async ({ resolvedData, operation }) => {
            if (operation === 'create' && resolvedData.pricingRule) {
                resolvedData.frozenPricingRule = await freezePricingRule(resolvedData.pricingRule)
            }
            return resolvedData
        },
        afterChange: async ({ operation, updatedItem, context }) => {
            // Only delete pending requests when a non-trial subscription context is created
            if (operation === 'create' && !updatedItem.isTrial) {
                const organizationId = updatedItem.organization
                
                // Soft delete all pending UserHelpRequests for this organization with activateSubscription type
                const pendingRequests = await find('UserHelpRequest', {
                    organization: { id: organizationId },
                    type: ACTIVATE_SUBSCRIPTION_TYPE,
                    deletedAt: null,
                })

                for (const request of pendingRequests) {
                    await UserHelpRequest.update(context, request.id, {
                        dv: 1,
                        sender: updatedItem.sender,
                        deletedAt: new Date().toISOString(),
                    })
                }
            }
        },
    },
    plugins: [uuided(), versioned(), tracked(), softDeleted(), dvAndSender(), historical()],
    access: {
        read: access.canReadSubscriptionContexts,
        create: access.canManageSubscriptionContexts,
        update: access.canManageSubscriptionContexts,
        delete: false,
        auth: true,
    },
})

module.exports = {
    SubscriptionContext,
}
