/**
 * Generated by `createschema subscription.SubscriptionContext 'organization:Relationship:Organization:CASCADE; subscriptionPlan:Relationship:SubscriptionPlan:PROTECT; startAt:DateTimeUtc; endAt:DateTimeUtc; basePrice:Decimal; calculatedPrice:Decimal; appliedRules:Json; manualPrice:Decimal; manualPriceReason:Text; isTrial:Checkbox;'`
 */

const { GQLError, GQLErrorCode: { BAD_USER_INPUT } } = require('@open-condo/keystone/errors')
const { historical, versioned, uuided, tracked, softDeleted, dvAndSender } = require('@open-condo/keystone/plugins')
const { GQLListSchema } = require('@open-condo/keystone/schema')

const { MONEY_AMOUNT_FIELD } = require('@condo/domains/common/schema/fields')
const { ORGANIZATION_OWNED_FIELD } = require('@condo/domains/organization/schema/fields')
const access = require('@condo/domains/subscription/access/SubscriptionContext')

const ERRORS = {
    END_DATE_MUST_BE_AFTER_START_DATE: {
        code: BAD_USER_INPUT,
        type: 'END_DATE_MUST_BE_AFTER_START_DATE',
        message: 'endAt must be after startAt',
    },
    BASE_PRICE_REQUIRED_FOR_NON_TRIAL: {
        code: BAD_USER_INPUT,
        type: 'BASE_PRICE_REQUIRED_FOR_NON_TRIAL',
        message: 'basePrice is required for non-trial subscriptions',
    },
    CALCULATED_PRICE_REQUIRED_FOR_NON_TRIAL: {
        code: BAD_USER_INPUT,
        type: 'CALCULATED_PRICE_REQUIRED_FOR_NON_TRIAL',
        message: 'calculatedPrice is required for non-trial subscriptions',
    },
    MANUAL_PRICE_REASON_REQUIRED: {
        code: BAD_USER_INPUT,
        type: 'MANUAL_PRICE_REASON_REQUIRED',
        message: 'manualPriceReason is required when manualPrice is set',
    },
}


const SubscriptionContext = new GQLListSchema('SubscriptionContext', {
    schemaDoc: 'Subscription context linking organization to a subscription plan with calculated pricing',
    fields: {

        organization: {
            ...ORGANIZATION_OWNED_FIELD,
            schemaDoc: 'Organization that has this subscription',
        },

        subscriptionPlan: {
            schemaDoc: 'Subscription plan selected by the organization',
            type: 'Relationship',
            ref: 'SubscriptionPlan',
            isRequired: true,
            knexOptions: { isNotNullable: true },
            kmigratorOptions: { null: false, on_delete: 'models.PROTECT' },
        },

        startAt: {
            schemaDoc: 'Subscription start date',
            type: 'DateTimeUtc',
            isRequired: true,
        },

        endAt: {
            schemaDoc: 'Subscription end date. If null, subscription is unlimited',
            type: 'DateTimeUtc',
            isRequired: false,
        },

        basePrice: {
            ...MONEY_AMOUNT_FIELD,
            schemaDoc: 'Base price from the subscription plan (before any rules applied). Not required for trial subscriptions',
            isRequired: false,
        },

        calculatedPrice: {
            ...MONEY_AMOUNT_FIELD,
            schemaDoc: 'Final calculated price after applying all pricing rules. Not required for trial subscriptions',
            isRequired: false,
        },

        appliedRules: {
            schemaDoc: 'JSON array of applied pricing rules with audit information',
            type: 'Json',
            isRequired: false,
            defaultValue: [],
        },

        manualPrice: {
            ...MONEY_AMOUNT_FIELD,
            schemaDoc: 'Manual price override by support. If set, this price will be used instead of calculatedPrice',
            isRequired: false,
        },

        manualPriceReason: {
            schemaDoc: 'Reason for manual price override. Required if manualPrice is set',
            type: 'Text',
            isRequired: false,
        },

        isTrial: {
            schemaDoc: 'Whether this is a trial subscription',
            type: 'Checkbox',
            defaultValue: false,
            isRequired: true,
        },

        daysRemaining: {
            schemaDoc: 'Number of days remaining in the subscription. Calculated from server time. Returns 0 if subscription has expired',
            type: 'Virtual',
            graphQLReturnType: 'Int!',
            resolver: (item) => {
                if (!item.endAt) return 0

                const now = new Date()
                const endAt = new Date(item.endAt)
                const diffMs = endAt.getTime() - now.getTime()

                if (diffMs <= 0) return 0

                return Math.ceil(diffMs / (1000 * 60 * 60 * 24))
            },
        },

    },
    hooks: {
        validateInput: async ({ resolvedData, existingItem, context }) => {
            const startAt = resolvedData.startAt || existingItem?.startAt
            const endAt = resolvedData.endAt || existingItem?.endAt
            const isTrial = resolvedData.isTrial !== undefined ? resolvedData.isTrial : existingItem?.isTrial
            const basePrice = resolvedData.basePrice !== undefined ? resolvedData.basePrice : existingItem?.basePrice
            const calculatedPrice = resolvedData.calculatedPrice !== undefined ? resolvedData.calculatedPrice : existingItem?.calculatedPrice

            // Validate dates
            if (startAt && endAt && new Date(startAt) >= new Date(endAt)) {
                throw new GQLError(ERRORS.END_DATE_MUST_BE_AFTER_START_DATE, context)
            }

            // Validate price is required for non-trial subscriptions
            if (!isTrial && (basePrice === null || basePrice === undefined)) {
                throw new GQLError(ERRORS.BASE_PRICE_REQUIRED_FOR_NON_TRIAL, context)
            }
            if (!isTrial && (calculatedPrice === null || calculatedPrice === undefined)) {
                throw new GQLError(ERRORS.CALCULATED_PRICE_REQUIRED_FOR_NON_TRIAL, context)
            }

            // Validate manual price reason
            const manualPrice = resolvedData.manualPrice !== undefined ? resolvedData.manualPrice : existingItem?.manualPrice
            const manualPriceReason = resolvedData.manualPriceReason !== undefined ? resolvedData.manualPriceReason : existingItem?.manualPriceReason

            if (manualPrice !== null && manualPrice !== undefined && !manualPriceReason) {
                throw new GQLError(ERRORS.MANUAL_PRICE_REASON_REQUIRED, context)
            }
        },
    },
    plugins: [uuided(), versioned(), tracked(), softDeleted(), dvAndSender(), historical()],
    access: {
        read: access.canReadSubscriptionContexts,
        create: access.canManageSubscriptionContexts,
        update: false,
        delete: false,
        auth: true,
    },
})

module.exports = {
    SubscriptionContext,
}
