/**
 * Generated by `createschema subscription.SubscriptionContext 'organization:Relationship:Organization:CASCADE; subscriptionPlan:Relationship:SubscriptionPlan:PROTECT; startAt:DateTimeUtc; endAt:DateTimeUtc; basePrice:Decimal; calculatedPrice:Decimal; appliedRules:Json; manualPrice:Decimal; manualPriceReason:Text; isTrial:Checkbox;'`
 */

const { historical, versioned, uuided, tracked, softDeleted, dvAndSender } = require('@open-condo/keystone/plugins')
const { GQLListSchema } = require('@open-condo/keystone/schema')

const { MONEY_AMOUNT_FIELD } = require('@condo/domains/common/schema/fields')
const access = require('@condo/domains/subscription/access/SubscriptionContext')


const SubscriptionContext = new GQLListSchema('SubscriptionContext', {
    schemaDoc: 'Subscription context linking organization to a subscription plan with calculated pricing',
    fields: {

        organization: {
            schemaDoc: 'Organization that has this subscription',
            type: 'Relationship',
            ref: 'Organization',
            isRequired: true,
            knexOptions: { isNotNullable: true },
            kmigratorOptions: { null: false, on_delete: 'models.CASCADE' },
        },

        subscriptionPlan: {
            schemaDoc: 'Subscription plan selected by the organization',
            type: 'Relationship',
            ref: 'SubscriptionPlan',
            isRequired: true,
            knexOptions: { isNotNullable: true },
            kmigratorOptions: { null: false, on_delete: 'models.PROTECT' },
        },

        startAt: {
            schemaDoc: 'Subscription start date',
            type: 'DateTimeUtc',
            isRequired: true,
        },

        endAt: {
            schemaDoc: 'Subscription end date',
            type: 'DateTimeUtc',
            isRequired: true,
        },

        basePrice: {
            ...MONEY_AMOUNT_FIELD,
            schemaDoc: 'Base price from the subscription plan (before any rules applied)',
            isRequired: true,
        },

        calculatedPrice: {
            ...MONEY_AMOUNT_FIELD,
            schemaDoc: 'Final calculated price after applying all pricing rules',
            isRequired: true,
        },

        appliedRules: {
            schemaDoc: 'JSON array of applied pricing rules with audit information',
            type: 'Json',
            isRequired: false,
            defaultValue: [],
        },

        manualPrice: {
            ...MONEY_AMOUNT_FIELD,
            schemaDoc: 'Manual price override by support. If set, this price will be used instead of calculatedPrice',
            isRequired: false,
        },

        manualPriceReason: {
            schemaDoc: 'Reason for manual price override. Required if manualPrice is set',
            type: 'Text',
            isRequired: false,
        },

        isTrial: {
            schemaDoc: 'Whether this is a trial subscription',
            type: 'Checkbox',
            defaultValue: false,
            isRequired: true,
        },

        daysRemaining: {
            schemaDoc: 'Number of days remaining in the subscription. Calculated from server time. Returns 0 if subscription has expired',
            type: 'Virtual',
            graphQLReturnType: 'Int!',
            resolver: (item) => {
                if (!item.endAt) return 0

                const now = new Date()
                const endAt = new Date(item.endAt)
                const diffMs = endAt.getTime() - now.getTime()

                if (diffMs <= 0) return 0

                return Math.ceil(diffMs / (1000 * 60 * 60 * 24))
            },
        },

    },
    hooks: {
        validateInput: async ({ resolvedData, existingItem, addValidationError }) => {
            const startAt = resolvedData.startAt || existingItem?.startAt
            const endAt = resolvedData.endAt || existingItem?.endAt

            // Validate dates
            if (startAt && endAt && new Date(startAt) >= new Date(endAt)) {
                addValidationError('endAt must be after startAt')
            }

            // Validate manual price reason
            const manualPrice = resolvedData.manualPrice !== undefined ? resolvedData.manualPrice : existingItem?.manualPrice
            const manualPriceReason = resolvedData.manualPriceReason !== undefined ? resolvedData.manualPriceReason : existingItem?.manualPriceReason

            if (manualPrice !== null && manualPrice !== undefined && !manualPriceReason) {
                addValidationError('manualPriceReason is required when manualPrice is set')
            }
        },
    },
    plugins: [uuided(), versioned(), tracked(), softDeleted(), dvAndSender(), historical()],
    access: {
        read: access.canReadSubscriptionContexts,
        create: access.canManageSubscriptionContexts,
        update: access.canManageSubscriptionContexts,
        delete: false,
        auth: true,
    },
})

module.exports = {
    SubscriptionContext,
}
