/**
 * Generated by `createschema subscription.SubscriptionPlan 'type:Select:basic,extended; periodType:Select:monthly,yearly; name:Text; description:Text; price:Decimal; currencyCode:Text; news:Checkbox; marketplace:Checkbox; support:Checkbox; ai:Checkbox; passTickets:Checkbox; isActive:Checkbox;'`
 */

const { GQLError, GQLErrorCode: { BAD_USER_INPUT } } = require('@open-condo/keystone/errors')
const { historical, versioned, uuided, tracked, softDeleted, dvAndSender } = require('@open-condo/keystone/plugins')
const { GQLListSchema, find } = require('@open-condo/keystone/schema')

const { ORGANIZATION_TYPES } = require('@condo/domains/organization/constants/common')
const access = require('@condo/domains/subscription/access/SubscriptionPlan')
const { SUBSCRIPTION_TYPES } = require('@condo/domains/subscription/constants')

const ERRORS = {
    DUPLICATE_ACTIVE_PLAN: {
        code: BAD_USER_INPUT,
        type: 'DUPLICATE_ACTIVE_PLAN',
        message: 'An active subscription plan with this type and organizationType already exists',
    },
}


const SubscriptionPlan = new GQLListSchema('SubscriptionPlan', {
    schemaDoc: 'Subscription plan that defines a product with features. Prices are defined via SubscriptionPlanPricingRule',
    fields: {

        type: {
            schemaDoc: 'Type of subscription plan (basic, extended). Used for programmatic access',
            type: 'Select',
            options: SUBSCRIPTION_TYPES,
            isRequired: true,
            access: {
                read: true,
                create: true,
                update: false,
            },
        },

        name: {
            schemaDoc: 'Human-readable name of the subscription plan',
            type: 'Text',
            isRequired: true,
        },

        description: {
            schemaDoc: 'Detailed description of the subscription plan',
            type: 'Text',
            isRequired: false,
        },

        organizationType: {
            schemaDoc: 'Type of organization this plan is available for',
            type: 'Select',
            options: ORGANIZATION_TYPES,
            dataType: 'string',
            isRequired: true,
            access: {
                read: true,
                create: true,
                update: false,
            },
        },

        isActive: {
            schemaDoc: 'Whether this plan is currently available for purchase',
            type: 'Checkbox',
            defaultValue: true,
            isRequired: true,
        },

        news: {
            schemaDoc: 'Whether news feature is included in this plan',
            type: 'Checkbox',
            defaultValue: false,
            isRequired: true,
        },

        marketplace: {
            schemaDoc: 'Whether marketplace feature is included in this plan',
            type: 'Checkbox',
            defaultValue: false,
            isRequired: true,
        },

        support: {
            schemaDoc: 'Whether support feature is included in this plan',
            type: 'Checkbox',
            defaultValue: false,
            isRequired: true,
        },

        ai: {
            schemaDoc: 'Whether AI feature is included in this plan',
            type: 'Checkbox',
            defaultValue: false,
            isRequired: true,
        },

        passTickets: {
            schemaDoc: 'Whether pass tickets feature is included in this plan',
            type: 'Checkbox',
            defaultValue: false,
            isRequired: true,
        },

    },
    hooks: {
        validateInput: async ({ resolvedData, existingItem, context }) => {
            const type = resolvedData.type || existingItem?.type
            const organizationType = resolvedData.organizationType || existingItem?.organizationType
            const isActive = resolvedData.isActive !== undefined ? resolvedData.isActive : existingItem?.isActive
            const currentId = existingItem?.id

            // Check uniqueness of type + organizationType for active plans
            if (isActive && type && organizationType) {
                const existingPlans = await find('SubscriptionPlan', {
                    type,
                    organizationType,
                    isActive: true,
                    deletedAt: null,
                    ...(currentId ? { id_not: currentId } : {}),
                })

                if (existingPlans.length > 0) {
                    throw new GQLError(ERRORS.DUPLICATE_ACTIVE_PLAN, context)
                }
            }
        },
    },
    plugins: [uuided(), versioned(), tracked(), softDeleted(), dvAndSender(), historical()],
    access: {
        read: access.canReadSubscriptionPlans,
        create: access.canManageSubscriptionPlans,
        update: access.canManageSubscriptionPlans,
        delete: false,
        auth: true,
    },
})

module.exports = {
    SubscriptionPlan,
}
