/**
 * Generated by `createschema subscription.SubscriptionPlanPricingRule 'name:Text; description:Text; subscriptionPlan:Relationship:SubscriptionPlan:CASCADE; period:Select:monthly,yearly; conditions:Json; price:Decimal; currencyCode:Text; priority:Integer; isHidden:Checkbox;'`
 */

const { GQLError, GQLErrorCode: { BAD_USER_INPUT } } = require('@open-condo/keystone/errors')
const { historical, versioned, uuided, tracked, softDeleted, dvAndSender, analytical } = require('@open-condo/keystone/plugins')
const { GQLListSchema } = require('@open-condo/keystone/schema')

const { MONEY_AMOUNT_FIELD, CURRENCY_CODE_FIELD } = require('@condo/domains/common/schema/fields')
const access = require('@condo/domains/subscription/access/SubscriptionPlanPricingRule')
const { SUBSCRIPTION_PERIODS } = require('@condo/domains/subscription/constants')
const { validateConditions } = require('@condo/domains/subscription/utils/conditionsEvaluator')

const ERRORS = {
    INVALID_CONDITIONS: {
        code: BAD_USER_INPUT,
        type: 'INVALID_CONDITIONS',
        message: 'Invalid conditions format. Expected { all: [...] } or { any: [...] } with conditions like { "all": [{ "fact": "organizationIds", "operator": "in", "value": ["org-id-1", "org-id-2"] }] }',
    },
    PRICE_AND_CURRENCY_MUST_BE_SET_TOGETHER: {
        code: BAD_USER_INPUT,
        type: 'PRICE_AND_CURRENCY_MUST_BE_SET_TOGETHER',
        message: 'price and currencyCode must be both set or both null',
    },
}

const SubscriptionPlanPricingRule = new GQLListSchema('SubscriptionPlanPricingRule', {
    schemaDoc: 'Pricing rule for subscription plan. Defines price for specific period and conditions',
    fields: {

        name: {
            schemaDoc: 'Human-readable name of the pricing rule',
            type: 'Text',
            isRequired: true,
        },

        description: {
            schemaDoc: 'Detailed description of the pricing rule',
            type: 'Text',
            isRequired: false,
        },

        subscriptionPlan: {
            schemaDoc: 'Subscription plan this rule applies to',
            type: 'Relationship',
            ref: 'SubscriptionPlan',
            isRequired: true,
            knexOptions: { isNotNullable: true },
            kmigratorOptions: { null: false, on_delete: 'models.CASCADE' },
        },

        period: {
            schemaDoc: 'Subscription period this rule applies to (month, year)',
            type: 'Select',
            options: SUBSCRIPTION_PERIODS,
            isRequired: true,
        },

        conditions: {
            schemaDoc: 'JSON conditions for rule matching (json-rules-engine format). Format: { all: [...] } or { any: [...] }. Each condition: { fact, operator, value }. Facts: organizationIds, organizationFeatures. Operators: equal, notEqual, in, notIn, contains. Example: { "all": [{ "fact": "organizationIds", "operator": "in", "value": ["org-id-1", "org-id-2"] }] }',
            type: 'Json',
            isRequired: false,
            defaultValue: null,
            hooks: {
                validateInput: async ({ resolvedData, fieldPath, context }) => {
                    const conditions = resolvedData[fieldPath]
                    if (conditions === null || conditions === undefined) return

                    const validationResult = validateConditions(conditions)
                    if (!validationResult.valid) {
                        throw new GQLError({
                            ...ERRORS.INVALID_CONDITIONS,
                            message: validationResult.error,
                        }, context)
                    }
                },
            },
        },

        price: {
            ...MONEY_AMOUNT_FIELD,
            schemaDoc: 'Price for this rule. Null means custom price (requires manual processing)',
            isRequired: false,
        },

        currencyCode: {
            ...CURRENCY_CODE_FIELD,
            schemaDoc: 'Currency code (ISO-4217)',
            isRequired: false,
        },

        priority: {
            schemaDoc: 'Rule priority. Higher priority rules are applied first',
            type: 'Integer',
            defaultValue: 0,
            isRequired: true,
        },

        isHidden: {
            schemaDoc: 'Whether this pricing rule is hidden from regular users. Hidden rules are only visible to admins and are not applied in price calculations.',
            type: 'Checkbox',
            defaultValue: false,
            isRequired: true,
        },

    },
    hooks: {
        validateInput: async ({ resolvedData, existingItem, context }) => {
            const price = resolvedData.price !== undefined ? resolvedData.price : existingItem?.price
            const currencyCode = resolvedData.currencyCode !== undefined ? resolvedData.currencyCode : existingItem?.currencyCode

            const hasPrice = price !== null && price !== undefined
            const hasCurrency = currencyCode !== null && currencyCode !== undefined && currencyCode !== ''

            if (hasPrice !== hasCurrency) {
                throw new GQLError(ERRORS.PRICE_AND_CURRENCY_MUST_BE_SET_TOGETHER, context)
            }
        },
    },
    plugins: [uuided(), versioned(), tracked(), softDeleted(), dvAndSender(), historical(), analytical()],
    access: {
        read: access.canReadSubscriptionPlanPricingRules,
        create: access.canManageSubscriptionPlanPricingRules,
        update: access.canManageSubscriptionPlanPricingRules,
        delete: false,
        auth: true,
    },
})

module.exports = {
    SubscriptionPlanPricingRule,
    ERRORS,
}
