/**
 * Generated by `createschema subscription.SubscriptionPlanPricingRule 'name:Text; description:Text; subscriptionPlan:Relationship:SubscriptionPlan:CASCADE; period:Select:monthly,yearly; currencyCode:Text; organization:Relationship:Organization:CASCADE; organizationFeatures:Json; discountPercent:Decimal; fixedPrice:Decimal; priority:Integer; isActive:Checkbox; canBePromoted:Checkbox; promotionText:Text;'`
 */

const { GQLError } = require('@open-condo/keystone/errors')
const { historical, versioned, uuided, tracked, softDeleted, dvAndSender } = require('@open-condo/keystone/plugins')
const { GQLListSchema } = require('@open-condo/keystone/schema')

const { MONEY_AMOUNT_FIELD, PERCENT_FIELD, CURRENCY_CODE_FIELD } = require('@condo/domains/common/schema/fields')
const { COMMON_AND_ORGANIZATION_OWNED_FIELD } = require('@condo/domains/organization/schema/fields')
const access = require('@condo/domains/subscription/access/SubscriptionPlanPricingRule')
const { SUBSCRIPTION_PERIODS } = require('@condo/domains/subscription/constants')
const { validateConditions } = require('@condo/domains/subscription/utils/conditionsEvaluator')

const ERRORS = {
    INVALID_PRICING_RULE_VALUES: {
        code: 'BAD_USER_INPUT',
        type: 'INVALID_PRICING_RULE_VALUES',
        message: 'You must specify either fixedPrice or discountPercent, but not both',
        messageForUser: 'api.subscription.SubscriptionPlanPricingRule.INVALID_PRICING_RULE_VALUES',
    },
    CURRENCY_REQUIRED_FOR_FIXED_PRICE: {
        code: 'BAD_USER_INPUT',
        type: 'CURRENCY_REQUIRED_FOR_FIXED_PRICE',
        message: 'currencyCode is required when fixedPrice is specified',
        messageForUser: 'api.subscription.SubscriptionPlanPricingRule.CURRENCY_REQUIRED_FOR_FIXED_PRICE',
    },
    INVALID_CONDITIONS: {
        code: 'BAD_USER_INPUT',
        type: 'INVALID_CONDITIONS',
        message: 'Invalid conditions format',
        messageForUser: 'api.subscription.SubscriptionPlanPricingRule.INVALID_CONDITIONS',
    },
}

const SubscriptionPlanPricingRule = new GQLListSchema('SubscriptionPlanPricingRule', {
    schemaDoc: 'Pricing rule that modifies subscription plan price based on conditions. Rule type is determined by which value field is set: fixedPrice or discountPercent',
    fields: {

        name: {
            schemaDoc: 'Human-readable name of the pricing rule',
            type: 'Text',
            isRequired: true,
        },

        description: {
            schemaDoc: 'Detailed description of the pricing rule',
            type: 'Text',
            isRequired: false,
        },

        subscriptionPlan: {
            schemaDoc: 'Subscription plan this rule applies to',
            type: 'Relationship',
            ref: 'SubscriptionPlan',
            isRequired: true,
            kmigratorOptions: { null: false, on_delete: 'models.CASCADE' },
        },

        period: {
            schemaDoc: 'Subscription period this rule applies to (monthly, yearly)',
            type: 'Select',
            options: SUBSCRIPTION_PERIODS,
            isRequired: true,
        },

        currencyCode: {
            ...CURRENCY_CODE_FIELD,
            schemaDoc: 'Currency code for fixed price (ISO-4217). Required when fixedPrice is specified',
            isRequired: false,
        },

        organization: {
            ...COMMON_AND_ORGANIZATION_OWNED_FIELD,
            schemaDoc: 'Specific organization this rule applies to. If null, applies to all organizations',
            isRequired: false,
        },

        conditions: {
            schemaDoc: 'JSON conditions for rule matching (json-rules-engine format). Format: { all: [...] } or { any: [...] }. Each condition: { fact, operator, value }. Facts: organizationIds, organizationFeatures. Operators: equal, notEqual, in, notIn, contains, doesNotContain',
            type: 'Json',
            isRequired: false,
            defaultValue: null,
            hooks: {
                validateInput: async ({ resolvedData, fieldPath, operation }) => {
                    const conditions = resolvedData[fieldPath]
                    if (conditions === null || conditions === undefined) return

                    const validationResult = validateConditions(conditions)
                    if (!validationResult.valid) {
                        throw new GQLError({
                            ...ERRORS.INVALID_CONDITIONS,
                            message: validationResult.error,
                        }, { operation })
                    }
                },
            },
        },

        discountPercent: {
            ...PERCENT_FIELD,
            schemaDoc: 'Discount percentage (0-100). Mutually exclusive with fixedPrice',
            isRequired: false,
        },

        fixedPrice: {
            ...MONEY_AMOUNT_FIELD,
            schemaDoc: 'Fixed price to set. Mutually exclusive with discountPercent. Requires currencyCode',
            isRequired: false,
        },

        priority: {
            schemaDoc: 'Rule priority. Higher priority rules are applied first. Default is 100',
            type: 'Integer',
            defaultValue: 100,
            isRequired: true,
        },

        isActive: {
            schemaDoc: 'Whether this rule is currently active',
            type: 'Checkbox',
            defaultValue: true,
            isRequired: true,
        },

        canBePromoted: {
            schemaDoc: 'Whether this rule can be shown as a promotion on the frontend',
            type: 'Checkbox',
            defaultValue: false,
            isRequired: true,
        },

        promotionText: {
            schemaDoc: 'Promotional text to display on the frontend when canBePromoted is true',
            type: 'Text',
            isRequired: false,
        },

    },
    hooks: {
        validateInput: async ({ resolvedData, existingItem, operation }) => {
            const fixedPrice = resolvedData.fixedPrice !== undefined ? resolvedData.fixedPrice : existingItem?.fixedPrice
            const discountPercent = resolvedData.discountPercent !== undefined ? resolvedData.discountPercent : existingItem?.discountPercent
            const currencyCode = resolvedData.currencyCode !== undefined ? resolvedData.currencyCode : existingItem?.currencyCode

            const hasFixedPrice = fixedPrice !== null && fixedPrice !== undefined
            const hasDiscountPercent = discountPercent !== null && discountPercent !== undefined

            // Must have exactly one of fixedPrice or discountPercent
            if ((hasFixedPrice && hasDiscountPercent) || (!hasFixedPrice && !hasDiscountPercent)) {
                throw new GQLError(ERRORS.INVALID_PRICING_RULE_VALUES, { operation })
            }

            // currencyCode is required for fixedPrice
            if (hasFixedPrice && !currencyCode) {
                throw new GQLError(ERRORS.CURRENCY_REQUIRED_FOR_FIXED_PRICE, { operation })
            }
        },
    },
    plugins: [uuided(), versioned(), tracked(), softDeleted(), dvAndSender(), historical()],
    access: {
        read: access.canReadSubscriptionPlanPricingRules,
        create: access.canManageSubscriptionPlanPricingRules,
        update: access.canManageSubscriptionPlanPricingRules,
        delete: false,
        auth: true,
    },
})

module.exports = {
    SubscriptionPlanPricingRule,
}
