const { isSoftDelete } = require('@open-condo/keystone/access')
const { get } = require('lodash')
const { getById, getByCondition } = require('@open-condo/keystone/schema')


async function canReadIncidentProperties ({ authentication: { item: user } }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin || user.isSupport) return {}

    return {
        incident: {
            organization: {
                OR: [
                    queryOrganizationEmployeeFor(user.id),
                    queryOrganizationEmployeeFromRelatedOrganizationFor(user.id),
                ],
                deletedAt: null,
            },
        },
    }
}

async function canManageIncidentProperties ({ authentication: { item: user }, originalInput, operation, itemId }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (operation === 'update' && !isSoftDelete(originalInput)) return false
    if (user.isAdmin || user.isSupport) return true

    let incidentId
    if (operation === 'create') {
        incidentId = get(originalInput, 'incident.connect.id')
    } else if (operation === 'update') {
        if (!itemId) return false
        const item = await getById('IncidentProperty', itemId)
        incidentId = get(item, 'incident', null)
    }
    if (!incidentId) return false
    const incident = await getById('Incident', incidentId)

    const organizationId = get(incident, 'organization', null)
    if (!organizationId) return false

    const employeeFromOrganization = await getByCondition('OrganizationEmployee', {
        organization: { id: organizationId },
        user: { id: user.id },
        deletedAt: null,
        isBlocked: false,
    })

    const employeeFromOrganizationLink = await getByCondition('OrganizationLink', {
        from: queryOrganizationEmployeeFor(user.id),
        to: { id: organizationId },
    })

    return Boolean(employeeFromOrganization) || Boolean(employeeFromOrganizationLink)
}

