/**
 * Generated by `createschema ticket.TicketAutoAssignment 'assignee:Relationship:OrganizationEmployee:SET_NULL;executor:Relationship:OrganizationEmployee:SET_NULL;classifier:Relationship:TicketClassifier:CASCADE;'`
 */

const get = require('lodash/get')

const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')
const { getById } = require('@open-condo/keystone/schema')

const {
    getEmployedOrRelatedOrganizationsByPermissions,
    checkPermissionsInEmployedOrRelatedOrganizations,
} = require('@condo/domains/organization/utils/accessSchema')
const { STAFF } = require('@condo/domains/user/constants/common')
const { canDirectlyReadSchemaObjects, canDirectlyManageSchemaObjects } = require('@condo/domains/user/utils/directAccess')


/**
 * TicketAutoAssignment can be read by
 * 1. Admin
 * 2. Support
 * 3. Users with direct access
 * 4. Employee who can manage tickets
 */
async function canReadTicketAutoAssignments ({ authentication: { item: user }, listKey, context }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    if (user.isAdmin || user.isSupport) return {}

    const hasDirectAccess = await canDirectlyReadSchemaObjects(user, listKey)
    if (hasDirectAccess) return {}

    if (user.type === STAFF) {
        const permittedOrganizations = await getEmployedOrRelatedOrganizationsByPermissions(context, user, 'canManageTickets')

        return {
            organization: {
                id_in: permittedOrganizations,
            },
        }
    }
    
    return false
}

/**
 * TicketAutoAssignment can be manage by
 * 1. Admin
 * 2. Support
 * 3. Users with direct access
 * 4. Employee who can manage ticket auto assignments
 */
async function canManageTicketAutoAssignments ({ authentication: { item: user }, originalInput, operation, itemId, listKey, context }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    if (user.isAdmin || user.isSupport) return true

    const hasDirectAccess = await canDirectlyManageSchemaObjects(user, listKey, originalInput, operation)
    if (hasDirectAccess) return true

    if (user.type === STAFF) {
        let organizationId
        
        if (operation === 'create') {
            organizationId = get(originalInput, 'organization.connect.id', null)
        } else if (operation === 'update') {
            if (!itemId) return false
            const item = await getById('TicketAutoAssignment', itemId)
            organizationId = get(item, 'organization', null)
        }
        
        if (!organizationId) return false

        return await checkPermissionsInEmployedOrRelatedOrganizations(context, user, organizationId, 'canManageTicketAutoAssignments')
    }

    return false
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadTicketAutoAssignments,
    canManageTicketAutoAssignments,
}
