/**
 * Generated by `createschema ticket.TicketChange 'ticket:Relationship:Ticket:CASCADE;'`
 */
const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')
const { getTicketAccessForUser } = require('@condo/domains/ticket/utils/accessSchema')

async function canReadTicketChanges ({ authentication: { item: user } }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    
    if (user.isSupport || user.isAdmin) return {}

    const ticketAccessObj = await getTicketAccessForUser(user)

    return {
        ticket: {
            ...ticketAccessObj,
        },
    }
}

async function canManageTicketChanges () {
    return false
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadTicketChanges,
    canManageTicketChanges,
}
