/**
 * Generated by `createschema ticket.TicketComment 'ticket:Relationship:Ticket:CASCADE; user:Relationship:User:CASCADE; content:Text;'`
 */

const { get, compact, isArray, uniq } = require('lodash')

const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')
const { getByCondition, getById } = require('@open-condo/keystone/schema')

const {
    canReadObjectsAsB2BAppServiceUser,
    canManageObjectsAsB2BAppServiceUser,
} = require('@condo/domains/miniapp/utils/b2bAppServiceUserAccess')
const {
    checkPermissionsInEmployedOrRelatedOrganizations,
    getEmployedOrRelatedOrganizationsByPermissions,
} = require('@condo/domains/organization/utils/accessSchema')
const { getUserResidents } = require('@condo/domains/resident/utils/accessSchema')
const {
    RESIDENT_COMMENT_TYPE,
    ORGANIZATION_COMMENT_TYPE,
    COMPLETED_STATUS_TYPE,
    CANCELED_STATUS_TYPE,
} = require('@condo/domains/ticket/constants')
const {
    getTicketFieldsMatchesResidentFieldsQuery,
} = require('@condo/domains/ticket/utils/accessSchema')
const { RESIDENT, SERVICE } = require('@condo/domains/user/constants/common')


async function canReadTicketComments (args) {
    const { authentication: { item: user }, context } = args

    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    if (user.isSupport || user.isAdmin) return {}

    if (user.type === SERVICE) {
        const accessFilter = await canReadObjectsAsB2BAppServiceUser(args)

        if (!accessFilter) return false

        return {
            ...accessFilter,
        }
    }

    if (user.type === RESIDENT) {
        const residents = await getUserResidents(context, user)

        const organizationsIds = compact(residents.map(resident => get(resident, 'organization')))
        const residentAddressOrStatement = getTicketFieldsMatchesResidentFieldsQuery(user, residents)

        return {
            type: RESIDENT_COMMENT_TYPE,
            ticket: {
                organization: {
                    id_in: uniq(organizationsIds),
                    deletedAt: null,
                },
                OR: [
                    { createdBy: { id: user.id } },
                    ...residentAddressOrStatement,
                ],
            },
        }
    }

    const permittedOrganizations = await getEmployedOrRelatedOrganizationsByPermissions(context, user, 'canReadTickets')


    return {
        ticket: {
            organization: {
                id_in: permittedOrganizations,
            },
        },
    }
}

const checkManageCommentAccess = async (args) => {
    const { authentication: { item: user }, originalInput, operation, itemId, context } = args

    if (user.type === SERVICE) {
        const hasAccess = await canManageObjectsAsB2BAppServiceUser(args)
        if (!hasAccess) return false

        // service user can't update not his own ticket comment
        if (operation === 'update') {
            const comment = await getByCondition('TicketComment', { id: itemId, deletedAt: null })

            if (!comment || comment.user !== user.id) return false
        }

        return true
    }

    if (user.type === RESIDENT) {
        if (operation === 'create') {
            const ticketId = get(originalInput, ['ticket', 'connect', 'id'])
            const ticket = await getByCondition('Ticket', { id: ticketId, deletedAt: null })
            const commentType = get(originalInput, 'type')

            if (!ticket || !commentType || commentType !== RESIDENT_COMMENT_TYPE) return false

            const ticketStatusId = get(ticket, 'status')
            const ticketStatus = await getById('TicketStatus', ticketStatusId)
            if (ticketStatus.type === COMPLETED_STATUS_TYPE || ticketStatus.type === CANCELED_STATUS_TYPE) {
                return false
            }

            return ticket.client === user.id
        }
        return false
    } else {
        if (operation === 'create') {
            const ticketId = get(originalInput, ['ticket', 'connect', 'id'])
            const ticket = await getByCondition('Ticket', { id: ticketId, deletedAt: null })
            if (!ticket) return false
            const organizationId = get(ticket, 'organization')
            if (!organizationId) return false

            return await checkPermissionsInEmployedOrRelatedOrganizations(context, user, organizationId, 'canManageTicketComments')
        } else if (operation === 'update' && itemId) {
            const comment = await getByCondition('TicketComment', { id: itemId, deletedAt: null })
            if (!comment || comment.user !== user.id) return false
            const ticket = await getByCondition('Ticket', { id: comment.ticket, deletedAt: null })
            if (!ticket) return false
            const organizationId = get(ticket, 'organization')
            if (!organizationId) return false

            return await checkPermissionsInEmployedOrRelatedOrganizations(context, user, organizationId, 'canManageTicketComments')
        }
    }

    return false
}

//TODO(DOMA-9337): Get rid of loop with request in bulk requests
async function canManageTicketComments (args) {
    const { authentication: { item: user }, originalInput, operation } = args

    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin) return true

    const isBulkRequest = isArray(originalInput)

    if (operation === 'update' && isBulkRequest) {
        return false
    }

    if (operation === 'create' && isBulkRequest) {
        for (const ticketCommentInputData of originalInput) {
            const ticketCommentInput = get(ticketCommentInputData, 'data')
            const accessToCreateComment = await checkManageCommentAccess({ ...args, originalInput: ticketCommentInput })

            if (!accessToCreateComment) {
                return false
            }
        }

        return true
    }

    return await checkManageCommentAccess(args)
}

async function canSetUserField ({ authentication: { item: user }, originalInput }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin) return true

    return get(originalInput, ['user', 'connect', 'id']) === user.id
}

async function canReadUserField ({ authentication: { item: user }, existingItem }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin || user.type !== RESIDENT) return true

    return get(existingItem, 'user') === user.id
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadTicketComments,
    canManageTicketComments,
    canSetUserField,
    canReadUserField,
}
