/**
 * Generated by `createschema ticket.TicketObserver 'ticket:Relationship:Ticket:CASCADE; user:Relationship:User:CASCADE'`
 */

const isEmpty = require('lodash/isEmpty')

const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')
const { getById } = require('@open-condo/keystone/schema')

const {
    getEmployedOrRelatedOrganizationsByPermissions,
    checkPermissionsInEmployedOrRelatedOrganizations,
} = require('@condo/domains/organization/utils/accessSchema')
const { getUserResidents } = require('@condo/domains/resident/utils/accessSchema')
const { RESIDENT, SERVICE } = require('@condo/domains/user/constants/common')


async function canReadTicketObservers (args) {
    const { authentication: { item: user }, context } = args

    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    if (user.isSupport || user.isAdmin) return {}

    if (user.type === SERVICE) return false

    if (user.type === RESIDENT) {
        const residents = await getUserResidents(context, user)

        if (isEmpty(residents)) return false

        return {
            ticket: {
                client: { id: user.id },
                canReadByResident: true,
            },
        }
    }

    const permittedOrganizations = await getEmployedOrRelatedOrganizationsByPermissions(context, user, 'canReadTickets')

    return {
        ticket: {
            organization: {
                id_in: permittedOrganizations,
            },
        },
    }
}

async function canManageTicketObservers (args) {
    const { authentication: { item: user }, originalInput, operation, itemId, context } = args

    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    if (user.isSupport || user.isAdmin) return true
    if (user.type === SERVICE) return false
    if (user.type === RESIDENT) return false

    if (Array.isArray(originalInput)) return false

    let ticketId = originalInput?.ticket?.connect?.id || null

    if (!ticketId && operation === 'update') {
        if (!itemId) return false
        const observer = await getById('TicketObserver', itemId)
        ticketId = observer?.ticket || null
    }

    if (!ticketId) return operation === 'create'

    const ticket = await getById('Ticket', ticketId)
    const organizationId = ticket?.organization || null

    if (!ticket || ticket.deletedAt || !organizationId) return false

    return await checkPermissionsInEmployedOrRelatedOrganizations(context, user, organizationId, 'canManageTickets')
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadTicketObservers,
    canManageTicketObservers,
}
