/**
 * Generated by `createschema ticket.CallRecord 'organization:Relationship:Organization:CASCADE;file?:File;callerPhone:Text;destCallerPhone:Text;talkTime:Integer;startedAt:DateTimeUtc;isIncomingCall:Checkbox;callId:Text;'`
 */

const { makeLoggedInAdminClient, makeClient } = require('@open-condo/keystone/test.utils')
const {
    expectToThrowAuthenticationErrorToObj, expectToThrowAuthenticationErrorToObjects,
    expectToThrowAccessDeniedErrorToObj,
} = require('@open-condo/keystone/test.utils')

const { createTestOrganization, createTestOrganizationEmployeeRole, createTestOrganizationEmployee } = require('@condo/domains/organization/utils/testSchema')
const { CallRecord, createTestCallRecord, updateTestCallRecord } = require('@condo/domains/ticket/utils/testSchema')
const { makeClientWithNewRegisteredAndLoggedInUser, makeClientWithSupportUser } = require('@condo/domains/user/utils/testSchema')


describe('CallRecord', () => {
    let admin, support, employeeUser, notEmployeeUser, anonymous, organization, callRecordByAdmin

    beforeAll(async () => {
        admin = await makeLoggedInAdminClient()
        support = await makeClientWithSupportUser()
        employeeUser = await makeClientWithNewRegisteredAndLoggedInUser()
        anonymous = await makeClient()

        const [testOrganization] = await createTestOrganization(admin)
        organization = testOrganization

        const [role] = await createTestOrganizationEmployeeRole(admin, organization, {
            canManageCallRecords: true,
        })
        await createTestOrganizationEmployee(admin, organization, employeeUser.user, role)

        notEmployeeUser = await makeClientWithNewRegisteredAndLoggedInUser()
        const [secondTestOrganization] = await createTestOrganization(admin)
        const [secondRole] = await createTestOrganizationEmployeeRole(admin, secondTestOrganization)
        await createTestOrganizationEmployee(admin, secondTestOrganization, notEmployeeUser.user, secondRole)
    })
    beforeEach(async () => {
        const [testCallRecord] = await createTestCallRecord(admin, organization)
        callRecordByAdmin = testCallRecord
    })
    describe('Accesses', () => {
        describe('Admin', () => {
            test('can create', async () => {
                expect(callRecordByAdmin).toBeDefined()
                expect(callRecordByAdmin).toHaveProperty('organization.id', organization.id)
                expect(callRecordByAdmin.callerPhone).toBeDefined()
                expect(callRecordByAdmin.destCallerPhone).toBeDefined()
                expect(callRecordByAdmin.talkTime).toBeDefined()
                expect(callRecordByAdmin.startedAt).toBeDefined()
                expect(callRecordByAdmin.isIncomingCall).toBeDefined()
                expect(callRecordByAdmin.callId).toBeDefined()
            })
            test('can read', async () => {
                const callRecord = await CallRecord.getOne(admin, { id: callRecordByAdmin.id }, { sortBy: ['updatedAt_DESC'] })
                expect(callRecord).toBeDefined()
                expect(callRecord).toHaveProperty('id', callRecordByAdmin.id)
            })
            test('can update', async () => {
                const [callRecord] = await updateTestCallRecord(admin, callRecordByAdmin.id, { isIncomingCall: false })
                expect(callRecord).toBeDefined()
                expect(callRecord).toHaveProperty('isIncomingCall', false)
            })
            test('can\'t delete', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await CallRecord.delete(admin, callRecordByAdmin.id)
                })
            })
        })

        describe('Support', () => {
            test('can create', async () => {
                const [callRecord] = await createTestCallRecord(support, organization)
                expect(callRecord).toBeDefined()
                expect(callRecord).toHaveProperty('organization.id', organization.id)
                expect(callRecordByAdmin.callerPhone).toBeDefined()
                expect(callRecordByAdmin.destCallerPhone).toBeDefined()
                expect(callRecordByAdmin.talkTime).toBeDefined()
                expect(callRecordByAdmin.startedAt).toBeDefined()
                expect(callRecordByAdmin.isIncomingCall).toBeDefined()
                expect(callRecordByAdmin.callId).toBeDefined()
            })
            test('can read', async () => {
                const callRecord = await CallRecord.getOne(support, { id: callRecordByAdmin.id }, { sortBy: ['updatedAt_DESC'] })
                expect(callRecord).toBeDefined()
                expect(callRecord).toHaveProperty('id', callRecordByAdmin.id)
            })
            test('can update', async () => {
                const [callRecord] = await updateTestCallRecord(support, callRecordByAdmin.id, { isIncomingCall: false })
                expect(callRecord).toBeDefined()
                expect(callRecord).toHaveProperty('isIncomingCall', false)
            })
            test('can\'t delete', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await CallRecord.delete(support, callRecordByAdmin.id)
                })
            })
        })

        describe('Employee', () => {
            test('can create', async () => {
                const [callRecord] = await createTestCallRecord(employeeUser, organization)
                expect(callRecord).toBeDefined()
                expect(callRecord).toHaveProperty('organization.id', organization.id)
                expect(callRecord.callerPhone).toBeDefined()
                expect(callRecord.destCallerPhone).toBeDefined()
                expect(callRecord.talkTime).toBeDefined()
                expect(callRecord.startedAt).toBeDefined()
                expect(callRecord.isIncomingCall).toBeDefined()
                expect(callRecord.callId).toBeDefined()
            })
            test('can read', async () => {
                const callRecord = await CallRecord.getOne(employeeUser, { id: callRecordByAdmin.id }, { sortBy: ['updatedAt_DESC'] })
                expect(callRecord).toBeDefined()
                expect(callRecord).toHaveProperty('id', callRecordByAdmin.id)
            })
            test('can update', async () => {
                const [callRecord] = await updateTestCallRecord(employeeUser, callRecordByAdmin.id, { isIncomingCall: false })
                expect(callRecord).toBeDefined()
                expect(callRecord).toHaveProperty('isIncomingCall', false)
            })
            test('can\'t delete', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await CallRecord.delete(employeeUser, callRecordByAdmin.id)
                })
            })
        })

        describe('Not employee', () => {
            test('can\'t create', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await createTestCallRecord(notEmployeeUser, organization)
                })
            })
            test('can\'t read', async () => {
                const callRecords = await CallRecord.getAll(notEmployeeUser, { id: callRecordByAdmin.id }, { sortBy: ['updatedAt_DESC'], first: 10 })
                expect(callRecords).toHaveLength(0)
            })
            test('can\'t update', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await updateTestCallRecord(notEmployeeUser, callRecordByAdmin.id)
                })
            })
            test('can\'t delete', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await CallRecord.delete(notEmployeeUser, callRecordByAdmin.id)
                })
            })
        })

        describe('Anonymous', () => {
            test('can\'t create', async () => {
                await expectToThrowAuthenticationErrorToObj(async () => {
                    await createTestCallRecord(anonymous, organization)
                })
            })
            test('can\'t read', async () => {
                await expectToThrowAuthenticationErrorToObjects(async () => {
                    await CallRecord.getOne(anonymous, { id: callRecordByAdmin.id }, { sortBy: ['updatedAt_DESC'] })
                })
            })
            test('can\'t update', async () => {
                await expectToThrowAuthenticationErrorToObj(async () => {
                    await updateTestCallRecord(anonymous, callRecordByAdmin.id)
                })
            })
            test('can\'t delete', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await CallRecord.delete(anonymous, callRecordByAdmin.id)
                })
            })
        })
    })
})
