/**
 * Generated by `createschema ticket.ExportTicketTask 'status:Select:processing,completed,error; format:Select:excel; exportedRecordsCount:Integer; totalRecordsCount:Integer; file?:File; meta?:Json'`
 */
const difference = require('lodash/difference')
const { makeLoggedInAdminClient, makeClient, UUID_RE, DATETIME_RE } = require('@core/keystone/test.utils')

const { ExportTicketTask, createTestExportTicketTask, updateTestExportTicketTask } = require('@condo/domains/ticket/utils/testSchema')
const {
    expectToThrowAuthenticationErrorToObj, expectToThrowAccessDeniedErrorToObj,
    expectToThrowAuthenticationErrorToObjects,
} = require('@condo/domains/common/utils/testSchema')
const { makeClientWithNewRegisteredAndLoggedInUser } = require('@condo/domains/user/utils/testSchema')
const { EXPORT_STATUS_VALUES } = require('@condo/domains/common/constants/export')

describe('ExportTicketTask', () => {
    describe('Create', () => {
        it('cannot be created by anonymous', async () => {
            const anonymousClient = await makeClient()
            const userClient = await makeClientWithNewRegisteredAndLoggedInUser()

            await expectToThrowAuthenticationErrorToObj(async () => {
                await createTestExportTicketTask(anonymousClient, userClient.user)
            })
        })

        it('cannot be created by user', async () => {
            const userClient = await makeClientWithNewRegisteredAndLoggedInUser()

            await expectToThrowAccessDeniedErrorToObj(async () => {
                await createTestExportTicketTask(userClient, userClient.user)
            })
        })

        it('can be created by admin', async () => {
            const adminClient = await makeLoggedInAdminClient()

            const [obj, attrs] = await createTestExportTicketTask(adminClient, adminClient.user)
            expect(obj.id).toMatch(UUID_RE)
            expect(obj.dv).toEqual(1)
            expect(obj.sender).toEqual(attrs.sender)
            expect(obj.v).toEqual(1)
            expect(obj.newId).toEqual(null)
            expect(obj.deletedAt).toEqual(null)
            expect(obj.createdBy).toEqual(expect.objectContaining({ id: adminClient.user.id }))
            expect(obj.updatedBy).toEqual(expect.objectContaining({ id: adminClient.user.id }))
            expect(obj.createdAt).toMatch(DATETIME_RE)
            expect(obj.updatedAt).toMatch(DATETIME_RE)
        })
    })

    describe('Read', () => {
        it('can be read by user who is associated with this record', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient1 = await makeClientWithNewRegisteredAndLoggedInUser()
            const userClient2 = await makeClientWithNewRegisteredAndLoggedInUser()
            const userClient3 = await makeClientWithNewRegisteredAndLoggedInUser()

            const [obj1] = await createTestExportTicketTask(adminClient, userClient1.user)
            const [obj2] = await createTestExportTicketTask(adminClient, userClient2.user)

            const records1 = await ExportTicketTask.getAll(userClient1)
            expect(records1).toHaveLength(1)
            expect(records1).toMatchObject([obj1])

            const records2 = await ExportTicketTask.getAll(userClient2)
            expect(records2).toHaveLength(1)
            expect(records2).toMatchObject([obj2])

            const records3 = await ExportTicketTask.getAll(userClient3)
            expect(records3).toHaveLength(0)
        })

        it('can be read by admin', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithNewRegisteredAndLoggedInUser()

            await createTestExportTicketTask(adminClient, adminClient.user)
            await createTestExportTicketTask(adminClient, userClient.user)

            const records = await ExportTicketTask.getAll(adminClient, {}, { sortBy: ['updatedAt_DESC'] })
            expect(records.length).toBeGreaterThanOrEqual(2)
        })

        it('cannot be read by anonymous', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const anonymousClient = await makeClient()

            await createTestExportTicketTask(adminClient, adminClient.user)

            await expectToThrowAuthenticationErrorToObjects(async () => {
                await ExportTicketTask.getAll(anonymousClient)
            })
        })
    })

    describe('Update', () => {
        it('can be updated by admin', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const [obj, attrs] = await createTestExportTicketTask(adminClient, adminClient.user)
            const newAttrs = {
                status: difference(EXPORT_STATUS_VALUES, [attrs.status])[0],
            }
            const [updatedObj] = await updateTestExportTicketTask(adminClient, obj.id, newAttrs)
            expect(updatedObj.status).toEqual(newAttrs.status)
        })

        it('cannot be updated by user', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithNewRegisteredAndLoggedInUser()
            const [obj, attrs] = await createTestExportTicketTask(adminClient, adminClient.user)
            const newAttrs = {
                status: difference(EXPORT_STATUS_VALUES, [attrs.status])[0],
            }
            await expectToThrowAccessDeniedErrorToObj(async () => {
                await updateTestExportTicketTask(userClient, obj.id, newAttrs)
            })
        })

        it('cannot be updated by anonymous', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const anonymousClient = await makeClient()
            const [obj, attrs] = await createTestExportTicketTask(adminClient, adminClient.user)
            const newAttrs = {
                status: difference(EXPORT_STATUS_VALUES, [attrs.status])[0],
            }
            await expectToThrowAuthenticationErrorToObj(async () => {
                await updateTestExportTicketTask(anonymousClient, obj.id, newAttrs)
            })
        })
    })

    describe('Delete', () => {

        it('can be soft-deleted by admin', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const [obj] = await createTestExportTicketTask(adminClient, adminClient.user)

            const [objUpdated, attrs] = await ExportTicketTask.softDelete(adminClient, obj.id)

            expect(objUpdated.id).toEqual(obj.id)
            expect(objUpdated.dv).toEqual(1)
            expect(objUpdated.sender).toEqual(attrs.sender)
            expect(objUpdated.deletedAt).toMatch(DATETIME_RE)
            expect(objUpdated.updatedAt).toMatch(DATETIME_RE)
            expect(objUpdated.updatedAt).not.toEqual(objUpdated.createdAt)
        })

        it('cannot be soft-deleted by user', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithNewRegisteredAndLoggedInUser()
            const [obj] = await createTestExportTicketTask(adminClient, adminClient.user)
            await expectToThrowAccessDeniedErrorToObj(async () => {
                await ExportTicketTask.softDelete(userClient, obj.id)
            })
        })

        it('cannot be soft-deleted by anonymous', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const anonymousClient = await makeClient()
            const [obj] = await createTestExportTicketTask(adminClient, adminClient.user)
            await expectToThrowAuthenticationErrorToObj(async () => {
                await ExportTicketTask.softDelete(anonymousClient, obj.id)
            })
        })

        it('cannot be deleted by admin', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const [obj] = await createTestExportTicketTask(adminClient, adminClient.user)
            await expectToThrowAccessDeniedErrorToObj(async () => {
                await ExportTicketTask.delete(adminClient, obj.id)
            })
        })

        it('cannot be deleted by user', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithNewRegisteredAndLoggedInUser()
            const [obj] = await createTestExportTicketTask(adminClient, adminClient.user)
            await expectToThrowAccessDeniedErrorToObj(async () => {
                await ExportTicketTask.delete(userClient, obj.id)
            })
        })

        it('cannot be deleted by anonymous', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const anonymousClient = await makeClient()
            const [obj] = await createTestExportTicketTask(adminClient, adminClient.user)
            await expectToThrowAccessDeniedErrorToObj(async () => {
                await ExportTicketTask.delete(anonymousClient, obj.id)
            })
        })
    })

})
