/**
 * Generated by `createschema ticket.IncidentTicketClassifier 'incident:Relationship:Incident:CASCADE; classifier:Relationship:TicketClassifier:PROTECT;'`
 */

const faker = require('faker')

const { makeLoggedInAdminClient, makeClient, catchErrorFrom } = require('@open-condo/keystone/test.utils')
const {
    expectToThrowAuthenticationErrorToObj, expectToThrowAuthenticationErrorToObjects,
    expectToThrowAccessDeniedErrorToObj,
} = require('@open-condo/keystone/test.utils')

const { createTestIncident, IncidentTicketClassifier, createTestIncidentTicketClassifier, updateTestIncidentTicketClassifier } = require('@condo/domains/ticket/utils/testSchema')
const { makeClientWithNewRegisteredAndLoggedInUser, makeClientWithSupportUser } = require('@condo/domains/user/utils/testSchema')

const {
    createTestOrganization,
    createTestOrganizationEmployeeRole,
    createTestOrganizationEmployee,
} = require('../../organization/utils/testSchema')


const DELETED_PAYLOAD = { deletedAt: 'true' }
const INCIDENT_PAYLOAD = {
    details: faker.lorem.sentence(),
    workStart: faker.date.recent().toISOString(),
}
const CLASSIFIER = {
    id: 'ff4ce74e-7011-41ea-a3ef-47233abe4330',
}

describe('IncidentTicketClassifier', () => {
    let admin
    let support
    let employeeUser
    let notEmployeeUser
    let anonymous
    let organization
    let incidentByAdmin
    let incidentClassifierByAdmin
    beforeAll(async () => {
        admin = await makeLoggedInAdminClient()
        support = await makeClientWithSupportUser()
        employeeUser = await makeClientWithNewRegisteredAndLoggedInUser()
        anonymous = await makeClient()

        const [testOrganization] = await createTestOrganization(admin)
        organization = testOrganization

        const [role] = await createTestOrganizationEmployeeRole(admin, organization)

        await createTestOrganizationEmployee(admin, organization, employeeUser.user, role)

        notEmployeeUser = await makeClientWithNewRegisteredAndLoggedInUser()
        const [secondTestOrganization] = await createTestOrganization(admin)
        const [secondRole] = await createTestOrganizationEmployeeRole(admin, secondTestOrganization)
        await createTestOrganizationEmployee(admin, secondTestOrganization, notEmployeeUser.user, secondRole)
    })
    beforeEach(async () => {
        const [testIncident] = await createTestIncident(admin, organization, INCIDENT_PAYLOAD)
        incidentByAdmin = testIncident

        const [testIncidentClassifier] = await createTestIncidentTicketClassifier(admin, incidentByAdmin, CLASSIFIER)
        incidentClassifierByAdmin = testIncidentClassifier
    })
    describe('Accesses', () => {
        describe('Admin', () => {
            test('can create', async () => {
                expect(incidentClassifierByAdmin).toBeDefined()
                expect(incidentClassifierByAdmin).toHaveProperty('classifier.id', CLASSIFIER.id)
                expect(incidentClassifierByAdmin).toHaveProperty('incident.id', incidentByAdmin.id)
            })
            test('can read', async () => {
                const incidentClassifier = await IncidentTicketClassifier.getOne(admin, { id: incidentClassifierByAdmin.id }, { sortBy: ['updatedAt_DESC'] })
                expect(incidentClassifier).toBeDefined()
                expect(incidentClassifier).toHaveProperty('id', incidentClassifierByAdmin.id)
            })
            test('can\'t update', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await updateTestIncidentTicketClassifier(admin, incidentClassifierByAdmin.id, {})
                })
            })
            test('can soft delete', async () => {
                const [incidentClassifier] = await updateTestIncidentTicketClassifier(admin, incidentClassifierByAdmin.id, DELETED_PAYLOAD)
                expect(incidentClassifier).toBeDefined()
                expect(incidentClassifier).toHaveProperty('id', incidentClassifierByAdmin.id)
                expect(incidentClassifier).toHaveProperty('deletedAt')
                expect(incidentClassifier.deletedAt).not.toBeNull()
            })
            test('can\'t delete', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await IncidentTicketClassifier.delete(admin, incidentClassifierByAdmin.id)
                })
            })
        })

        describe('Support', () => {
            test('can create', async () => {
                const [incident] = await createTestIncident(admin, organization, INCIDENT_PAYLOAD)
                const [incidentClassifier] = await createTestIncidentTicketClassifier(support, incident, CLASSIFIER)
                expect(incidentClassifier).toBeDefined()
                expect(incidentClassifier).toHaveProperty('classifier.id', CLASSIFIER.id)
                expect(incidentClassifier).toHaveProperty('incident.id', incident.id)
            })
            test('can read', async () => {
                const incidentClassifier = await IncidentTicketClassifier.getOne(support, { id: incidentClassifierByAdmin.id }, { sortBy: ['updatedAt_DESC'] })
                expect(incidentClassifier).toBeDefined()
                expect(incidentClassifier).toHaveProperty('id', incidentClassifierByAdmin.id)
            })
            test('can\'t update', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await updateTestIncidentTicketClassifier(support, incidentClassifierByAdmin.id, {})
                })
            })
            test('can soft delete', async () => {
                const [incidentClassifier] = await updateTestIncidentTicketClassifier(support, incidentClassifierByAdmin.id, DELETED_PAYLOAD)
                expect(incidentClassifier).toBeDefined()
                expect(incidentClassifier).toHaveProperty('id', incidentClassifierByAdmin.id)
                expect(incidentClassifier).toHaveProperty('deletedAt')
                expect(incidentClassifier.deletedAt).not.toBeNull()
            })
            test('can\'t delete', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await IncidentTicketClassifier.delete(support, incidentClassifierByAdmin.id)
                })
            })
        })

        describe('Employee', () => {
            test('can create', async () => {
                const [incident] = await createTestIncident(admin, organization, INCIDENT_PAYLOAD)
                const [incidentClassifier] = await createTestIncidentTicketClassifier(employeeUser, incident, CLASSIFIER)
                expect(incidentClassifier).toBeDefined()
                expect(incidentClassifier).toHaveProperty('classifier.id', CLASSIFIER.id)
                expect(incidentClassifier).toHaveProperty('incident.id', incident.id)
            })
            test('can read', async () => {
                const incidentClassifier = await IncidentTicketClassifier.getOne(employeeUser, { id: incidentClassifierByAdmin.id }, { sortBy: ['updatedAt_DESC'] })
                expect(incidentClassifier).toBeDefined()
                expect(incidentClassifier).toHaveProperty('id', incidentClassifierByAdmin.id)
            })
            test('can\'t update', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await updateTestIncidentTicketClassifier(employeeUser, incidentClassifierByAdmin.id, {})
                })
            })
            test('can soft delete', async () => {
                const [incidentClassifier] = await updateTestIncidentTicketClassifier(employeeUser, incidentClassifierByAdmin.id, DELETED_PAYLOAD)
                expect(incidentClassifier).toBeDefined()
                expect(incidentClassifier).toHaveProperty('id', incidentClassifierByAdmin.id)
                expect(incidentClassifier).toHaveProperty('deletedAt')
                expect(incidentClassifier.deletedAt).not.toBeNull()
            })
            test('can\'t delete', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await IncidentTicketClassifier.delete(employeeUser, incidentClassifierByAdmin.id)
                })
            })
        })

        describe('Not employee', () => {
            test('can\'t create', async () => {
                const [incident] = await createTestIncident(admin, organization, INCIDENT_PAYLOAD)
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await createTestIncidentTicketClassifier(notEmployeeUser, incident, CLASSIFIER)
                })
            })
            test('can\'t read', async () => {
                const incidents = await IncidentTicketClassifier.getAll(notEmployeeUser, { id: incidentClassifierByAdmin.id }, { sortBy: ['updatedAt_DESC'], first: 10 })
                expect(incidents).toHaveLength(0)
            })
            test('can\'t update', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await updateTestIncidentTicketClassifier(notEmployeeUser, incidentClassifierByAdmin.id, DELETED_PAYLOAD)
                })
            })
            test('can\'t delete', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await IncidentTicketClassifier.delete(notEmployeeUser, incidentClassifierByAdmin.id)
                })
            })
        })

        describe('Anonymous', () => {
            test('can\'t create', async () => {
                await expectToThrowAuthenticationErrorToObj(async () => {
                    await createTestIncidentTicketClassifier(anonymous, incidentByAdmin, CLASSIFIER)
                })
            })
            test('can\'t read', async () => {
                await expectToThrowAuthenticationErrorToObjects(async () => {
                    await IncidentTicketClassifier.getOne(anonymous, { id: incidentClassifierByAdmin.id }, { sortBy: ['updatedAt_DESC'] })
                })
            })
            test('can\'t update', async () => {
                await expectToThrowAuthenticationErrorToObj(async () => {
                    await updateTestIncidentTicketClassifier(anonymous, incidentClassifierByAdmin.id, {})
                })
            })
            test('can\'t delete', async () => {
                await expectToThrowAccessDeniedErrorToObj(async () => {
                    await IncidentTicketClassifier.delete(anonymous, incidentClassifierByAdmin.id)
                })
            })
        })
    })

    describe('Validations', () => {
        test('Constraint: unique incident + classifier', async () => {
            await catchErrorFrom(async () => {
                await createTestIncidentTicketClassifier(admin, incidentByAdmin, CLASSIFIER)
            }, ({ errors }) => {
                expect(errors).toHaveLength(1)
                expect(errors[0]).toEqual(expect.objectContaining({
                    message: expect.stringContaining('unique constraint'),
                }))
            })
        })
    })
})
