/**
 * Generated by `createschema ticket.TicketFile 'organization:Text;file?:File;ticket:Relationship:Ticket:SET_NULL;'`
 */

const { Relationship, File } = require('@keystonejs/fields')
const { GQLListSchema } = require('@core/keystone/schema')
const { historical, versioned, uuided, tracked, softDeleted } = require('@core/keystone/plugins')
const { SENDER_FIELD, DV_FIELD } = require('@condo/domains/common/schema/fields')
const access = require('@condo/domains/ticket/access/TicketFile')
const { ORGANIZATION_OWNED_FIELD } = require('../../../schema/_common')

const { SberCloudFileAdapter } = require('@condo/domains/common/utils/sberCloudFileAdapter')
const S3Config = process.env.SBERCLOUD_OBS_CONFIG ? JSON.parse(process.env.SBERCLOUD_OBS_CONFIG) : {}
const fileAdapter = new SberCloudFileAdapter({ ...S3Config, folder: 'ticket' })

const TicketFile = new GQLListSchema('TicketFile', {
    schemaDoc: 'File attached to the ticket',
    fields: {
        dv: DV_FIELD,
        sender: SENDER_FIELD,
        organization: ORGANIZATION_OWNED_FIELD,
        file: {
            schemaDoc: 'File object with meta information and publicUrl',
            type: File,
            adapter: fileAdapter,
            isRequired: false,
        },
        ticket: {
            schemaDoc: 'Link to ticket',
            type: Relationship,
            ref: 'Ticket.files',
            many: false,
            isRequired: false,
            knexOptions: { isNotNullable: false }, // Maybe ticketFile without ticket connection on create
            kmigratorOptions: { null: true, on_delete: 'models.SET_NULL' },
        },
    },
    hooks: {
        afterDelete: async ({ existingItem }) => {
            if (existingItem.file) {
                await fileAdapter.delete(existingItem.file)
            }
        },
    },
    plugins: [uuided(), versioned(), tracked(), softDeleted(), historical()],
    access: {
        read: access.canReadTicketFiles,
        create: access.canManageTicketFiles,
        update: access.canManageTicketFiles,
        delete: access.canManageTicketFiles,
        auth: true,
    },
})

module.exports = {
    TicketFile,
}
