/**
 * Generated by `createschema ticket.TicketObserver 'ticket:Relationship:Ticket:CASCADE; user:Relationship:User:CASCADE'`
 */

const { GQLError, GQLErrorCode: { BAD_USER_INPUT } } = require('@open-condo/keystone/errors')
const { historical, versioned, uuided, tracked, softDeleted, dvAndSender } = require('@open-condo/keystone/plugins')
const { GQLListSchema } = require('@open-condo/keystone/schema')

const access = require('@condo/domains/ticket/access/TicketObserver')

const ERRORS = {
    TICKET_REQUIRED: {
        code: BAD_USER_INPUT,
        type: 'TICKET_REQUIRED',
        message: 'Ticket is required for TicketObserver create',
        messageForUser: 'api.ticket.ticketObserver.TICKET_REQUIRED',
    },
}

const TicketObserver = new GQLListSchema('TicketObserver', {
    schemaDoc: 'Employee/user who will be notified about the ticket',
    fields: {

        ticket: {
            schemaDoc: 'Link to ticket',
            type: 'Relationship',
            ref: 'Ticket.observers',
            isRequired: false,
            knexOptions: { isNotNullable: false }, // Relationship only!
            kmigratorOptions: { null: true, on_delete: 'models.SET_NULL' },
        },

        user: {
            schemaDoc: 'Link to employee/user',
            type: 'Relationship',
            ref: 'User',
            isRequired: true,
            knexOptions: { isNotNullable: true }, // Required relationship only!
            kmigratorOptions: { null: false, on_delete: 'models.CASCADE' },
        },

    },
    hooks: {
        validateInput: async ({ existingItem, resolvedData, context }) => {
            if (existingItem) return
            
            // We require `ticket` only for direct TicketObserver creation.
            // When TicketObserver is created via nested mutation from Ticket.observers.create,
            // Keystone doesn't pass `ticket` in resolvedData (it will be connected implicitly),
            // so we must not block that case.
            const ticketRel = resolvedData.ticket
            if (!ticketRel) {
                
                // TODO: Replace this with a reliable way to detect nested create vs direct mutation.
                // Current check relies on client-provided operationName and is not robust.
                const operationName = context?.req?.body?.operationName || ''
                const isDirectTicketObserverCreate = typeof operationName === 'string' && operationName.includes('TicketObserver')

                if (isDirectTicketObserverCreate) {
                    throw new GQLError(ERRORS.TICKET_REQUIRED, context)
                }
            }
        },
        // NOTE: afterChange is not used for this list, because we need build a ticket change history
    },
    kmigratorOptions: {
        constraints: [
            {
                type: 'models.UniqueConstraint',
                fields: ['ticket', 'user'],
                condition: 'Q(deletedAt__isnull=True)',
                name: 'ticket_observer_unique_ticket_and_user',
            },
        ],
    },
    plugins: [uuided(), versioned(), tracked(), softDeleted(), dvAndSender(), historical()],
    access: {
        read: access.canReadTicketObservers,
        create: access.canManageTicketObservers,
        update: access.canManageTicketObservers,
        delete: false,
        auth: true,
    },
})

module.exports = {
    ERRORS,
    TicketObserver,
}
