const { SERVICE } = require('../constants/common')

async function canReadExternalTokenAccessRights ({ authentication: { item: user } }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false

    if (user.isAdmin) return {}

    if (user.type === SERVICE) return {
        user: { id: user.id },
        deletedAt: null,
    }

    return false
}

async function canManageExternalTokenAccessRights ({ authentication: { item: user } }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin) return true

    return false
}

