/**
 * Generated by `createservice user.SigninOrRegisterResidentService`
 */
const { getSchemaCtx } = require('@core/keystone/schema')
const { GQLCustomSchema } = require('@core/keystone/schema')
const access = require('@condo/domains/user/access/SigninOrRegisterResidentService')
const {
    CONFIRM_PHONE_ACTION_EXPIRED,
} = require('@condo/domains/user/constants/errors')
const { ConfirmPhoneAction, User } = require('@condo/domains/user/utils/serverSchema')

const SigninOrRegisterResidentService = new GQLCustomSchema('SigninOrRegisterResidentService', {
    types: [
        {
            access: true,
            type: 'input SigninOrRegisterResidentInput { dv: Int!, sender: JSON!, token: String! }',
        },
        {
            access: true,
            type: 'type SigninOrRegisterResidentOutput { item: User, token: String! }',
        },
    ],

    mutations: [
        {
            access: access.canSigninOrRegisterResident,
            schema: 'signinOrRegisterResident(data: SigninOrRegisterResidentInput!): SigninOrRegisterResidentOutput',
            resolver: async (parent, args, context, info, extra = {}) => {
                const { data: { token } } = args
                const userData = {
                    type: 'resident',
                    isPhoneVerified: false,
                }
                const [action] = await ConfirmPhoneAction.getAll(context,
                    {
                        token,
                        completedAt: null,
                        isPhoneVerified: true,
                    }
                )
                if (!action) {
                    throw new Error(`${CONFIRM_PHONE_ACTION_EXPIRED}] Unable to find confirm phone action`)
                }
                const { phone, isPhoneVerified } = action
                userData.phone = phone
                userData.isPhoneVerified = isPhoneVerified
                let [user] = await User.getAll(context, { type: 'resident', phone })
                if (!user) {
                    user = await User.create(context, userData)
                }
                await ConfirmPhoneAction.update(context, action.id, { completedAt: new Date().toISOString() })
                const { keystone } = await getSchemaCtx(SigninOrRegisterResidentService)
                const sessionToken = await context.startAuthedSession({ item: user, list: keystone.lists['User'] })
                const result = {
                    item: user,
                    token: sessionToken,
                }
                return result
            },
        },
    ],

})

module.exports = {
    SigninOrRegisterResidentService,
}
