const { makeClient, catchErrorFrom } = require('@open-condo/keystone/test.utils')

const { userExternalIdentityUserFormCallbackByTestClient } = require('@condo/domains/user/utils/testSchema')
const { SBBOL_IDP_TYPE } = require('@condo/domains/user/constants/common')

describe('UserExternalIdentityUserFormCallbackService', () => {
    test('Sbbol case - should throw an error', async () => {
        const client = await makeClient()
        const payload = { identityType: SBBOL_IDP_TYPE, params: {} }
        await catchErrorFrom(async () => {
            await userExternalIdentityUserFormCallbackByTestClient(client, payload)
        }, ({ errors }) => {
            expect(errors).toMatchObject([{
                message: 'Identity integration does not support login form',
                path: ['result'],
                extensions: {
                    mutation: 'userExternalIdentityUserFormCallback',
                    variable: ['identityType'],
                    code: 'BAD_USER_INPUT',
                    type: 'IDENTITY_INTEGRATION_DOES_NOT_SUPPORT_LOGIN_FORM',
                    message: 'Identity integration does not support login form',
                },
            }])
        })
    })
})