/**
 * Generated by `createschema user.ConfirmEmailAction 'email:Text; isVerified:Checkbox; expiresAt:DateTimeUtc'`
 */

const dayjs = require('dayjs')

const access = require('@open-condo/keystone/access')
const { historical, versioned, uuided, tracked, softDeleted, dvAndSender } = require('@open-condo/keystone/plugins')
const { GQLListSchema } = require('@open-condo/keystone/schema')

const { EMAIL_VERIFICATION_TTL_IN_SEC } = require('@dev-api/domains/user/constants')



const ConfirmEmailAction = new GQLListSchema('ConfirmEmailAction', {
    schemaDoc: 'Internal schema used for email confirmation',
    fields: {
        email: {
            schemaDoc: 'Email to be confirmed',
            type: 'Text',
            isRequired: true,
        },
        isVerified: {
            schemaDoc: 'Determines if the mail has been verified',
            type: 'Checkbox',
            isRequired: true,
            defaultValue: false,
        },
        expiresAt: {
            schemaDoc: 'Action expiration date,',
            type: 'DateTimeUtc',
            isRequired: true,
            hooks: {
                resolveInput: () => {
                    return dayjs().add(EMAIL_VERIFICATION_TTL_IN_SEC, 'second').toISOString()
                },
            },
        },
    },
    plugins: [uuided(), versioned(), tracked(), softDeleted(), dvAndSender(), historical()],
    access: {
        read: access.userIsAdmin,
        create: false,
        update: false,
        delete: false,
        auth: true,
    },
})

module.exports = {
    ConfirmEmailAction,
}
