/**
 * Generated by `createservice miniapp.UpdateB2BAppContextService`
 */
const { throwAuthenticationError } = require('@open-condo/keystone/apolloErrorFormatter')
const { getByCondition } = require('@open-condo/keystone/schema')

const { REMOTE_SYSTEM } = require('@dev-portal-api/domains/common/constants/common')
const { productionClient, developmentClient } = require('@dev-portal-api/domains/common/utils/serverClients')
const { CondoB2BAppContextGql } = require('@dev-portal-api/domains/condo/gql')
const { PROD_ENVIRONMENT } = require('@dev-portal-api/domains/miniapp/constants/publishing')

async function canUpdateB2BAppContext ({ authentication: { item: user }, args }) {
    if (!user) return throwAuthenticationError()
    if (user.deletedAt) return false
    if (user.isAdmin || user.isSupport) return true

    const { data: { id, environment } } = args

    const serverClient = environment === PROD_ENVIRONMENT
        ? productionClient
        : developmentClient

    const foundContexts = await serverClient.getModels({
        modelGql: CondoB2BAppContextGql,
        where: {
            id,
            app: { importRemoteSystem: REMOTE_SYSTEM },
        },
        first: 1,
    })
    if (!foundContexts || !foundContexts.length) return false

    const context = foundContexts[0]
    if (!context || !context.app || !context.app.importId) return false

    const app = await getByCondition('B2BApp', { id: context.app.importId, deletedAt: null })

    return Boolean(app && app.createdBy === user.id)
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canUpdateB2BAppContext,
}