import type { CodegenConfig } from '@graphql-codegen/cli'


const codegenCommentPlugin = () => ({
    add: {
        content: [
            '// This file is autogenerated by @graphql-codegen/cli',
            '// Do not edit / add anything manually, since it will be overridden by codegen\n',
        ],
    },
})

const exportFromPlugin = (from: string) => {
    return {
        add: {
            content: `\nexport * from '${from}'\n`,
        },
    }
}

const config: CodegenConfig = {
    overwrite: true,
    schema: '../condo/schema.graphql',
    documents: 'domains/*/queries/*.graphql',
    generates: {
        'gql/operation.types.ts': {
            preset: 'import-types',
            presetConfig: {
                typesPath: '@app/condo/schema',
            },
            plugins: [
                codegenCommentPlugin(),
                'typescript-operations',
            ],
        },
        'gql/index.ts': {
            preset: 'import-types',
            presetConfig: {
                typesPath: '@app/helpdesk-web/gql/operation.types',
            },
            plugins: [
                codegenCommentPlugin(),
                'typescript-react-apollo',
                exportFromPlugin('@app/helpdesk-web/gql/operation.types'),
            ],
        },
    },
}

export default config
